import React, { useRef, useEffect, useState } from 'react';
import * as cam from '@mediapipe/camera_utils';
import { Holistic } from '@mediapipe/holistic';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';

function BodyMeasurementFrontView() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [status, setStatus] = useState('Initializing camera...');

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext('2d');

    const holistic = new Holistic({
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/holistic/${file}`,
    });

    holistic.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      refineFaceLandmarks: true,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    holistic.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(
        results.image,
        0,
        0,
        canvasElement.width,
        canvasElement.height
      );

      if (results.poseLandmarks) {
        drawConnectors(canvasCtx, results.poseLandmarks, window.POSE_CONNECTIONS,
          { color: '#00FF00', lineWidth: 2 });
        drawLandmarks(canvasCtx, results.poseLandmarks,
          { color: '#FF0000', lineWidth: 1 });
        setStatus('Landmarks detected!');
      } else {
        setStatus('No pose detected...');
      }

      canvasCtx.restore();
    });

    const camera = new cam.Camera(videoElement, {
      onFrame: async () => {
        await holistic.send({ image: videoElement });
      },
      width: 640,
      height: 480,
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, []);

  return (
    <div className="min-h-screen bg-[#0e1129] flex flex-col items-center justify-center text-white">
      <h2 className="text-2xl md:text-3xl font-bold text-purple-400 mb-6">
        AI Body Measurement (Front View)
      </h2>
      <div className="relative rounded-lg overflow-hidden shadow-lg">
        <video ref={videoRef} className="absolute" style={{ display: 'none' }} playsInline muted autoPlay />
        <canvas ref={canvasRef} width={640} height={480} />
      </div>
      <p className={`mt-4 ${status.includes('detected') ? 'text-green-400' : 'text-red-400'}`}>
        {status}
      </p>
    </div>
  );
}

export default BodyMeasurementFrontView;
