// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);
  const rafRef = useRef(null);

  const [locked, setLocked] = useState(false);
  const [status, setStatus] = useState("Show your shoulders and upper torso");
  const [result, setResult] = useState(null); // { tshirt, hoodie, fit, metrics }

  // ---- config you can tune ----
  const STABILITY_FRAMES = 18;      // how many frames must be stable
  const MOVEMENT_EPS = 0.012;       // allowed change in normalized shoulder width
  const GUIDE_MARGIN = 0.08;        // how tight the guide box is (0..0.2)

  // Size thresholds for MALE tops using ratio = shoulderWidth / torsoHeight (both normalized)
  // Start point; adjust after testing with a few people.
  const SIZE_THRESHOLDS = {
    S: 0.95,
    M: 1.05,
    L: 1.15,
    // XL: >1.15
  };

  const getDistance = useCallback((a, b) => Math.hypot(a.x - b.x, a.y - b.y), []);
  const midpoint = (a, b) => ({ x: (a.x + b.x) / 2, y: (a.y + b.y) / 2 });

  function pickSizeFromRatio(r) {
    if (r < SIZE_THRESHOLDS.S) return "S";
    if (r < SIZE_THRESHOLDS.M) return "M";
    if (r < SIZE_THRESHOLDS.L) return "L";
    return "XL";
  }

  function fitFromRatio(r) {
    if (r < SIZE_THRESHOLDS.S) return "Slim";
    if (r < SIZE_THRESHOLDS.M) return "Regular";
    if (r < SIZE_THRESHOLDS.L) return "Regular/Relaxed";
    return "Relaxed";
  }

  // stability buffer
  const recentWidthsRef = useRef([]);

  function stopCamera() {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      const s = v?.srcObject;
      if (s) s.getTracks().forEach((t) => t.stop());
      if (v) v.srcObject = null;
    } catch (_) {}
    if (rafRef.current) cancelAnimationFrame(rafRef.current);
  }

  async function startCamera(pose) {
    // Use MediaPipe Camera helper (desktop webcam)
    const videoEl = videoRef.current;
    const camera = new Camera(videoEl, {
      onFrame: async () => {
        await pose.send({ image: videoEl });
      },
      width: 1280,
      height: 720,
    });
    camRef.current = camera;
    camera.start();
  }

  useEffect(() => {
    const videoEl = videoRef.current;
    const canvasEl = canvasRef.current;
    const ctx = canvasEl.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });
    poseRef.current = pose;

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((res) => {
      // auto‑exit after lock
      if (locked) return;

      const W = res.image.width;
      const H = res.image.height;
      if (canvasEl.width !== W) canvasEl.width = W;
      if (canvasEl.height !== H) canvasEl.height = H;

      ctx.save();
      ctx.clearRect(0, 0, W, H);
      ctx.drawImage(res.image, 0, 0, W, H);

      // guide box (no cropping UI)
      const gx = W * GUIDE_MARGIN;
      const gy = H * GUIDE_MARGIN;
      const gW = W * (1 - GUIDE_MARGIN * 2);
      const gH = H * (1 - GUIDE_MARGIN * 2);
      ctx.strokeStyle = "rgba(0,255,0,0.8)";
      ctx.lineWidth = 3;
      ctx.strokeRect(gx, gy, gW, gH);

      const lm = res.poseLandmarks;
      if (!lm) {
        setStatus("Detecting upper body…");
        ctx.restore();
        return;
      }

      const vis = (i) => (lm[i]?.visibility ?? 0) > 0.5;
      const shouldersOK = vis(11) && vis(12);
      const hipsOK = vis(23) || vis(24);

      if (!(shouldersOK && hipsOK)) {
        setStatus("Show both shoulders and upper torso");
        ctx.restore();
        return;
      }

      // landmarks we need
      const Lsh = lm[11], Rsh = lm[12];
      const hip = vis(23) ? lm[23] : lm[24];
      const shMid = midpoint(Lsh, Rsh);

      // are key points inside the guide box?
      const toPx = (p) => ({ x: p.x * W, y: p.y * H });
      const pts = [Lsh, Rsh, shMid, hip].map(toPx);
      const insideGuide = pts.every(
        (p) => p.x >= gx && p.x <= gx + gW && p.y >= gy && p.y <= gy + gH
      );
      if (!insideGuide) {
        setStatus("Step back/center yourself inside the frame");
        ctx.restore();
        return;
      }

      // compute normalized metrics (independent of resolution)
      const shoulderWidthN = getDistance(Lsh, Rsh);        // normalized 0..1
      const torsoHeightN = getDistance(shMid, hip);        // normalized 0..1
      if (torsoHeightN <= 0) {
        setStatus("Hold still…");
        ctx.restore();
        return;
      }
      const ratio = shoulderWidthN / torsoHeightN;

      // stability check over recent frames
      const buf = recentWidthsRef.current;
      buf.push(shoulderWidthN);
      if (buf.length > STABILITY_FRAMES) buf.shift();

      if (buf.length >= STABILITY_FRAMES) {
        const avg =
          buf.reduce((a, b) => a + b, 0) / buf.length;
        const maxDev = Math.max(...buf.map((v) => Math.abs(v - avg)));
        if (maxDev < MOVEMENT_EPS) {
          // LOCK: decide sizes
          const tshirt = pickSizeFromRatio(ratio);
          // Hoodie usually same or 1 up for comfort
          const hoodie = tshirt === "S" ? "S" : tshirt; // change to size-up: tshirt -> next size
          const fit = fitFromRatio(ratio);

          setResult({
            tshirt,
            hoodie,
            fit,
            metrics: {
              ratio: Number(ratio.toFixed(3)),
              shoulderWidthNorm: Number(shoulderWidthN.toFixed(3)),
              torsoHeightNorm: Number(torsoHeightN.toFixed(3)),
            },
            notes:
              "Based on shoulder width relative to torso height. Tune thresholds after a few test users.",
          });

          setLocked(true);
          setStatus("Locked ✔️");
          stopCamera();
        } else {
          setStatus("Hold still for a moment…");
        }
      } else {
        setStatus("Framing looks good — keep still…");
      }

      // minimal points visualization (shoulders/hip/mid)
      ctx.fillStyle = "rgba(0, 200, 255, 0.9)";
      [Lsh, Rsh, shMid, hip].forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x * W, p.y * H, 6, 0, Math.PI * 2);
        ctx.fill();
      });

      ctx.restore();
    });

    startCamera(pose);

    return () => {
      stopCamera();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleRetake = () => {
    setLocked(false);
    setResult(null);
    recentWidthsRef.current = [];
    setStatus("Show your shoulders and upper torso");
    // restart camera
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="relative w-full min-h-screen bg-black flex flex-col items-center">
      {/* Header row */}
      <div className="w-full max-w-5xl flex items-center justify-between px-3 pt-3">
        <div className="text-white/90 font-semibold">Male Tops Sizing (Auto)</div>
        <div className="bg-white/90 rounded px-3 py-1 text-sm">{status}</div>
      </div>

      {/* Camera area (no cropping) */}
      <div className="relative w-full max-w-5xl aspect-video mt-3">
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-contain bg-black"
          autoPlay
          muted
          playsInline
        />
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full object-contain pointer-events-none"
        />

        {/* Lock overlay */}
        {locked && result && (
          <div className="absolute inset-0 bg-black/60 flex items-center justify-center p-4">
            <div className="bg-white max-w-md w-full rounded-2xl p-5 shadow-xl">
              <div className="text-xl font-bold mb-2">Recommended Sizes</div>
              <div className="space-y-1">
                <div>👕 T‑Shirt: <b>{result.tshirt}</b></div>
                <div>🧥 Hoodie: <b>{result.hoodie}</b></div>
                <div>🎯 Fit: <b>{result.fit}</b></div>
              </div>

              <div className="mt-3 text-xs text-gray-600">
                Debug: ratio={result.metrics.ratio}, shoulderN={result.metrics.shoulderWidthNorm}, torsoN={result.metrics.torsoHeightNorm}
              </div>

              <div className="mt-4 flex gap-2">
                <button
                  onClick={handleRetake}
                  className="px-4 py-2 rounded bg-black text-white hover:opacity-90"
                >
                  Retake
                </button>
                <button
                  onClick={() => window.print()}
                  className="px-4 py-2 rounded bg-purple-600 text-white hover:bg-purple-700"
                >
                  Save / Print
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="mt-2 text-white/60 text-sm">
        Tip: stand centered; keep shoulders level; hold still 1–2 seconds.
      </div>
    </div>
  );
};

export default Measure;
