import React, { useRef, useEffect } from 'react';

const ShirtSizeMeasurementSideView = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("Camera access denied:", error);
      }
    };

    startCamera();
  }, []);

  return (
    <section className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
      <h1 className="text-3xl font-bold text-purple-700 mb-4">
        Shirt Size Measurement (Side View)
      </h1>

      <div className="relative w-[480px] h-[360px]">
        <video
          ref={videoRef}
          autoPlay
          playsInline
          className="w-full h-full rounded-lg shadow-md"
        />
        <canvas
          ref={canvasRef}
          className="absolute top-0 left-0 w-full h-full"
        />
      </div>

      <p className="mt-4 text-gray-700 text-sm">
        Stand sideways like the demo. Size estimation in progress...
      </p>
    </section>
  );
};

export default ShirtSizeMeasurementSideView;
