import React, { useRef, useEffect, useState } from 'react';

const BodyMeasurementFrontView = () => {
  const videoRef = useRef(null);
  const [landmarksDetected, setLandmarksDetected] = useState(false);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        const video = videoRef.current;
        if (video) {
          video.srcObject = stream;

          // ✅ Wait for video metadata to load before playing
          video.onloadedmetadata = () => {
            video.play().catch((err) => {
              console.warn('Autoplay was prevented:', err);
            });
          };
        }

        // ✅ Simulated detection
        setTimeout(() => setLandmarksDetected(true), 2000);
      } catch (err) {
        console.error('Camera access denied or error:', err);
      }
    };

    startCamera();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center px-4">
      <h1 className="text-3xl font-bold text-purple-400 mb-6">AI Body Measurement (Front View)</h1>
      <video
        ref={videoRef}
        className="w-full max-w-md rounded-lg shadow-lg"
        autoPlay
        playsInline
        muted
      />
      <p className="mt-4 text-sm text-green-400">
        {landmarksDetected ? 'Landmarks detected!' : 'Detecting landmarks...'}
      </p>
    </div>
  );
};

export default BodyMeasurementFrontView;
