import React, { useEffect, useRef, useState } from 'react';

const BodyMeasurement = () => {
  const videoRef = useRef(null);
  const [cameraActive, setCameraActive] = useState(true);
  const [stream, setStream] = useState(null);

  useEffect(() => {
    startCamera();
  }, []);

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);
      setCameraActive(true);
    } catch (err) {
      console.error('Error accessing camera', err);
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setCameraActive(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-center py-10 px-4">
      <h1 className="text-3xl font-bold text-purple-700 mb-6">AI Body Measurement</h1>

      <div className="flex justify-center">
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          className="rounded-xl shadow-lg max-w-full w-[600px]"
        />
      </div>

      <p className="mt-4 text-green-600 font-medium">Landmarks detected!</p>

      <div className="mt-6 space-x-4">
        {cameraActive ? (
          <button
            onClick={stopCamera}
            className="bg-red-600 text-white px-6 py-2 rounded-full hover:bg-red-700 transition"
          >
            Stop Camera
          </button>
        ) : (
          <button
            onClick={startCamera}
            className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700 transition"
          >
            Restart Camera
          </button>
        )}
      </div>
    </div>
  );
};

export default BodyMeasurement;
