import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";
import PoseOverlay from "../components/PoseOverlay";

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [landmarksDetected, setLandmarksDetected] = useState(false);
  const [poseView, setPoseView] = useState("front");
  const [measurements, setMeasurements] = useState({ chest: null, waist: null });
  const [userHeight, setUserHeight] = useState(170); // in cm
  const [suggestedSize, setSuggestedSize] = useState("");

  const getDistance = useCallback((a, b) => {
    const dx = a.x - b.x;
    const dy = a.y - b.y;
    return Math.sqrt(dx * dx + dy * dy);
  }, []);

  const getSizeSuggestion = (chest, waist) => {
    if (chest > 95 || waist > 85) return "XL";
    if (chest > 90 || waist > 80) return "L";
    if (chest > 85 || waist > 75) return "M";
    if (chest > 80 || waist > 70) return "S";
    return "Custom";
  };

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext("2d");

    const pose = new Pose({
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      if (results.poseLandmarks) {
        drawConnectors(canvasCtx, results.poseLandmarks, Pose.POSE_CONNECTIONS, {
          color: "#00FF00",
          lineWidth: 2,
        });
        drawLandmarks(canvasCtx, results.poseLandmarks, {
          color: "#FF0000",
          lineWidth: 1,
        });

        const lm = results.poseLandmarks;
        const isVisible = (id) => lm[id]?.visibility > 0.7;

        if (
          isVisible(11) && isVisible(12) && // shoulders
          isVisible(23) && isVisible(24) && // hips
          isVisible(0) && isVisible(27)     // nose to ankle
        ) {
          setLandmarksDetected(true);

          const shoulderWidth = getDistance(lm[11], lm[12]);
          const hipWidth = getDistance(lm[23], lm[24]);
          const fullBodyHeight = getDistance(lm[0], lm[27]);

          if (fullBodyHeight > 0) {
            const pxToCmRatio = userHeight / fullBodyHeight;
            const chestCm = shoulderWidth * pxToCmRatio;
            const waistCm = hipWidth * pxToCmRatio;

            setMeasurements({
              chest: chestCm.toFixed(1),
              waist: waistCm.toFixed(1),
            });

            setSuggestedSize(getSizeSuggestion(chestCm, waistCm));
          }
        } else {
          setLandmarksDetected(false);
          setMeasurements({ chest: null, waist: null });
          setSuggestedSize("");
        }
      } else {
        setLandmarksDetected(false);
      }

      canvasCtx.restore();
    });

    const camera = new Camera(videoElement, {
      onFrame: async () => {
        await pose.send({ image: videoElement });
      },
      width: 640,
      height: 480,
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, [getDistance, userHeight]);

  return (
    <div className="relative w-full h-screen bg-black flex justify-center items-center">
      {/* User Height Input */}
      <input
        type="number"
        value={userHeight}
        onChange={(e) => setUserHeight(Number(e.target.value))}
        placeholder="Enter your height (cm)"
        className="absolute top-4 left-4 z-20 p-2 bg-white rounded shadow-md text-sm w-48"
      />

      {/* Video Feed */}
      <video ref={videoRef} className="absolute w-full h-full object-cover" autoPlay muted playsInline />
      <canvas ref={canvasRef} className="absolute w-full h-full" />

      {/* Pose Overlay Box */}
      <PoseOverlay view={poseView} />

      {/* Detection Status */}
      <div className="absolute bottom-4 left-4 text-white font-semibold">
        {landmarksDetected ? "✅ Full body detected!" : "🔍 Please show your full body"}
      </div>

      {/* Measurement Box */}
      {landmarksDetected && measurements.chest && (
        <div className="absolute top-4 right-4 bg-white/90 text-black p-4 rounded-lg shadow-md text-sm">
          <div className="text-lg font-bold mb-1">📏 Measurements</div>
          <div>👕 Chest: {measurements.chest} cm</div>
          <div>👖 Waist: {measurements.waist} cm</div>
          <div className="mt-1 text-purple-700 font-semibold">
            👗 Suggested Size: {suggestedSize}
          </div>
        </div>
      )}
    </div>
  );
};

export default Measure;
