import React, { useRef, useEffect, useState } from 'react';
import { Camera } from '@mediapipe/camera_utils';
import { Pose } from '@mediapipe/pose';
import { drawLandmarks } from '@mediapipe/drawing_utils';
import PoseOverlay from '../components/PoseOverlay';

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [step, setStep] = useState('front');
  const [landmarksDetected, setLandmarksDetected] = useState(false);
  const [fullBodyVisible, setFullBodyVisible] = useState(true);
  const [measurementData, setMeasurementData] = useState({
    front: null,
    side: null,
    back: null,
  });

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext('2d');

    const pose = new Pose({
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      if (results.poseLandmarks) {
        setLandmarksDetected(true);

        drawLandmarks(canvasCtx, results.poseLandmarks, {
          color: 'red',
          lineWidth: 2,
          radius: 3,
        });

        const hasHips = results.poseLandmarks[23] && results.poseLandmarks[24];
        const hasAnkles = results.poseLandmarks[27] && results.poseLandmarks[28];
        const isFull = hasHips && hasAnkles;
        setFullBodyVisible(isFull);

        if (isFull && !measurementData[step]) {
          setMeasurementData((prev) => ({
            ...prev,
            [step]: {
              timestamp: Date.now(),
              landmarks: results.poseLandmarks,
            },
          }));

          setTimeout(() => {
            if (step === 'front') setStep('side');
            else if (step === 'side') setStep('back');
            else if (step === 'back') setStep('result');
          }, 3000);
        }
      } else {
        setLandmarksDetected(false);
        setFullBodyVisible(true);
      }

      canvasCtx.restore();
    });

    if (videoElement) {
      const camera = new Camera(videoElement, {
        onFrame: async () => {
          await pose.send({ image: videoElement });
        },
        width: 640,
        height: 480,
      });
      camera.start();
    }
  }, [step, measurementData]);

  return (
    <div className="min-h-screen bg-black flex flex-col items-center justify-center text-white px-4 py-6">
      <p className="text-sm text-gray-300 mb-2">
        Tip: For best results, step 2–3 meters back until your whole body fits in the box.
      </p>
      <h1 className="text-3xl font-bold mb-4 text-purple-400">AI Body Measurement</h1>

      <div className="relative w-[640px] h-[480px] border border-purple-600 rounded-lg">
        <video ref={videoRef} className="absolute w-full h-full" autoPlay playsInline muted />
        <canvas ref={canvasRef} width="640" height="480" className="absolute top-0 left-0" />

        {/* Distance Guide Overlay */}
        {step !== 'result' && (
          <div className="absolute top-[10%] left-[10%] w-[80%] h-[80%] border-4 border-dashed border-purple-400 rounded-xl animate-pulse pointer-events-none z-20" />
        )}

        {/* Step Instruction Overlay */}
        {step !== 'result' && <PoseOverlay view={step} />}
      </div>

      <p className="mt-4 text-green-400">
        {landmarksDetected ? '✅ Body landmarks detected!' : '🔍 Looking for body...'}
      </p>

      {!fullBodyVisible && (
        <div className="mt-3 text-red-500 font-semibold text-md animate-bounce text-center">
          ⚠️ Please stand 2–3 meters away so your full body is inside the purple box
        </div>
      )}

      {step === 'result' && (
        <div className="mt-6 bg-white text-black p-6 rounded-lg shadow-lg w-full max-w-lg">
          <h2 className="text-xl font-bold text-purple-700 mb-2">📏 Measurement Summary</h2>
          <ul className="text-sm space-y-1">
            <li>✅ Front pose captured: {measurementData.front ? 'Yes' : 'No'}</li>
            <li>✅ Side pose captured: {measurementData.side ? 'Yes' : 'No'}</li>
            <li>✅ Back pose captured: {measurementData.back ? 'Yes' : 'No'}</li>
          </ul>
          <div className="mt-4 text-sm text-gray-700">
            (Clothing size and dimensions will be calculated here based on AI model.)
          </div>
        </div>
      )}
    </div>
  );
};

export default Measure;
