// src/pages/Measure.jsx
import React, { useEffect, useRef, useState } from 'react';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { drawKeypoints } from '../utils/landmarkUtils';

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [status, setStatus] = useState('Initializing...');

  useEffect(() => {
    let detector;

    const runPoseDetection = async () => {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      await videoRef.current.play();

      detector = await posedetection.createDetector(posedetection.SupportedModels.MoveNet);
      setStatus('Detecting...');

      const detect = async () => {
        if (!videoRef.current || videoRef.current.readyState !== 4) {
          requestAnimationFrame(detect);
          return;
        }

        const poses = await detector.estimatePoses(videoRef.current);
        const ctx = canvasRef.current.getContext('2d');
        canvasRef.current.width = videoRef.current.videoWidth;
        canvasRef.current.height = videoRef.current.videoHeight;
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

        if (poses.length > 0) {
          drawKeypoints(poses[0].keypoints, ctx);
          setStatus('Landmarks detected!');
        } else {
          setStatus('No pose detected');
        }

        requestAnimationFrame(detect);
      };

      detect();
    };

    runPoseDetection();
  }, []);

  return (
    <div className="min-h-screen bg-[#0f1123] flex flex-col items-center justify-center p-6">
      <h2 className="text-2xl md:text-3xl text-purple-400 font-semibold mb-4">
        AI Body Measurement (Front View)
      </h2>

      <div className="relative">
        <video ref={videoRef} className="rounded-lg" playsInline muted />
        <canvas ref={canvasRef} className="absolute top-0 left-0 rounded-lg" />
      </div>

      <p className="mt-4 text-green-400 font-medium">{status}</p>
    </div>
  );
};

export default Measure;
