// src/pages/Measure.jsx
import React, { useEffect, useRef, useState } from 'react';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';
import { Pose } from '@mediapipe/pose';

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [landmarksDetected, setLandmarksDetected] = useState(false);
  const [size, setSize] = useState('');

  useEffect(() => {
    const runPoseDetection = async () => {
      const detector = await posedetection.createDetector(posedetection.SupportedModels.BlazePose, {
        runtime: 'tfjs',
        modelType: 'lite',
      });

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }

      const detect = async () => {
        if (videoRef.current.readyState === 4) {
          const poses = await detector.estimatePoses(videoRef.current);
          const ctx = canvasRef.current.getContext('2d');
          canvasRef.current.width = videoRef.current.videoWidth;
          canvasRef.current.height = videoRef.current.videoHeight;
          ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

          if (poses[0]?.keypoints) {
            drawLandmarks(ctx, poses[0].keypoints, { color: 'red', radius: 5 });
            setLandmarksDetected(true);

            // Get chest width in pixels (between left & right shoulders)
            const leftShoulder = poses[0].keypoints.find(k => k.name === 'left_shoulder');
            const rightShoulder = poses[0].keypoints.find(k => k.name === 'right_shoulder');
            if (leftShoulder && rightShoulder && leftShoulder.score > 0.5 && rightShoulder.score > 0.5) {
              const dx = leftShoulder.x - rightShoulder.x;
              const dy = leftShoulder.y - rightShoulder.y;
              const pixelDistance = Math.sqrt(dx * dx + dy * dy);

              // Convert to cm (rough estimate): assume 96 pixels = ~40cm
              const cmDistance = (pixelDistance / 96) * 40;

              // Predict shirt size
              let shirtSize = '';
              if (cmDistance < 36) shirtSize = 'S';
              else if (cmDistance < 42) shirtSize = 'M';
              else if (cmDistance < 48) shirtSize = 'L';
              else shirtSize = 'XL';

              setSize(`${shirtSize} (Chest: ${cmDistance.toFixed(1)} cm)`);
            }
          }
        }
        requestAnimationFrame(detect);
      };
      detect();
    };

    runPoseDetection();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-black text-white">
      <h1 className="text-3xl font-bold text-purple-400 mb-4">AI Body Landmarks</h1>
      <div className="relative w-fit border-2 border-purple-500">
        <video ref={videoRef} className="rounded" />
        <canvas ref={canvasRef} className="absolute top-0 left-0" />
      </div>
      {landmarksDetected && (
        <p className="text-green-400 mt-2">Landmarks detected!</p>
      )}
      {size && (
        <p className="text-xl mt-4 text-yellow-400">Estimated Shirt Size: <span className="font-bold">{size}</span></p>
      )}
    </div>
  );
};

export default Measure;
