// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";
import PoseOverlay from "../components/PoseOverlay";

const poseSteps = ["front", "side", "back"];

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [userHeight, setUserHeight] = useState(170);
  const [landmarksDetected, setLandmarksDetected] = useState(false);
  const [measurements, setMeasurements] = useState({ chest: null, waist: null });
  const [suggestedSize, setSuggestedSize] = useState("");
  const [capturedImages, setCapturedImages] = useState({});

  const getDistance = useCallback((a, b) => {
    const dx = a.x - b.x;
    const dy = a.y - b.y;
    return Math.sqrt(dx * dx + dy * dy);
  }, []);

  const getSizeSuggestion = (chest, waist) => {
    if (chest > 95 || waist > 85) return "XL";
    if (chest > 90 || waist > 80) return "L";
    if (chest > 85 || waist > 75) return "M";
    if (chest > 80 || waist > 70) return "S";
    return "Custom";
  };

  const handleCapture = () => {
    const canvas = canvasRef.current;
    const dataUrl = canvas.toDataURL("image/png");
    const step = poseSteps[currentStep];
    setCapturedImages((prev) => ({ ...prev, [step]: dataUrl }));

    if (currentStep < poseSteps.length - 1) {
      setCurrentStep((s) => s + 1);
    } else {
      alert("✅ All poses captured!");
    }
  };

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      const lm = results.poseLandmarks;

      // ✅ Match canvas to incoming video frame for sharp, correct coords
      const frameW = results.image.width;
      const frameH = results.image.height;
      if (canvasElement.width !== frameW) canvasElement.width = frameW;
      if (canvasElement.height !== frameH) canvasElement.height = frameH;

      canvasCtx.save();
      canvasCtx.clearRect(0, 0, frameW, frameH);
      canvasCtx.drawImage(results.image, 0, 0, frameW, frameH);

      if (lm) {
        // Silhouette overlay
        const pts = [lm[11], lm[13], lm[15], lm[23], lm[24], lm[16], lm[14], lm[12], lm[0]].map((p) => ({
          x: p.x * frameW,
          y: p.y * frameH,
        }));
        canvasCtx.fillStyle = "rgba(0, 255, 255, 0.3)";
        canvasCtx.beginPath();
        pts.forEach((pt, i) => (i === 0 ? canvasCtx.moveTo(pt.x, pt.y) : canvasCtx.lineTo(pt.x, pt.y)));
        canvasCtx.closePath();
        canvasCtx.fill();

        // Pose lines + landmarks
        drawConnectors(canvasCtx, lm, Pose.POSE_CONNECTIONS, { lineWidth: 2 });
        drawLandmarks(canvasCtx, lm, { lineWidth: 1 });

        // ✅ More forgiving full-body check (either ankle) and slightly lower vis threshold
        const vis = (id) => (lm[id]?.visibility ?? 0) > 0.5;
        const coreOK = vis(11) && vis(12) && vis(23) && vis(24) && vis(0);
        const feetOK = vis(27) || vis(28);

        if (coreOK && feetOK) {
          setLandmarksDetected(true);

          const shoulderWidth = getDistance(lm[11], lm[12]);
          const hipWidth = getDistance(lm[23], lm[24]);

          // Use whichever ankle is visible for height
          const h1 = getDistance(lm[0], lm[27]);
          const h2 = getDistance(lm[0], lm[28]);
          const fullBodyHeight = Math.max(h1, h2);

          if (fullBodyHeight > 0) {
            const pxToCmRatio = userHeight / fullBodyHeight;
            const chestCm = shoulderWidth * pxToCmRatio;
            const waistCm = hipWidth * pxToCmRatio;

            setMeasurements({
              chest: chestCm.toFixed(1),
              waist: waistCm.toFixed(1),
            });
            setSuggestedSize(getSizeSuggestion(chestCm, waistCm));
          }
        } else {
          setLandmarksDetected(false);
          setMeasurements({ chest: null, waist: null });
          setSuggestedSize("");
        }
      }
      canvasCtx.restore();
    });

    // ✅ Higher resolution for better full-body detection
    const camera = new Camera(videoElement, {
      onFrame: async () => {
        await pose.send({ image: videoElement });
      },
      width: 1280,
      height: 720,
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, [getDistance, userHeight, currentStep]);

  return (
    <div className="relative w-full min-h-screen bg-black flex flex-col items-center">
      {/* Top controls (outside video box) */}
      <div className="w-full max-w-5xl flex items-center justify-between px-3 pt-2">
        <input
          type="number"
          value={userHeight}
          onChange={(e) => setUserHeight(Number(e.target.value))}
          placeholder="Enter your height (cm)"
          className="z-30 p-2 bg-white rounded shadow-md text-sm w-44"
        />
        <div className="z-30 bg-white/90 px-3 py-1 rounded text-sm font-medium">
          Step {currentStep + 1} of {poseSteps.length}: {poseSteps[currentStep].toUpperCase()} Pose
        </div>
      </div>

      {/* ✅ 16:9 wrapper so we see the whole camera frame without cropping */}
      <div className="relative w-full max-w-5xl aspect-video mt-2">
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-contain bg-black"
          autoPlay
          muted
          playsInline
        />
        <canvas ref={canvasRef} className="absolute inset-0 w-full h-full object-contain" />

        {/* Your pose guide overlay */}
        <PoseOverlay view={poseSteps[currentStep]} />

        {/* Status + Capture UI inside the video area */}
        <div className="absolute bottom-4 left-4 text-white font-semibold">
          {landmarksDetected ? "✅ Full body detected!" : "🔍 Please step back until head & feet are visible"}
        </div>

        <button
          onClick={handleCapture}
          className="absolute bottom-4 right-4 bg-purple-600 text-white px-4 py-2 rounded shadow-md hover:bg-purple-700"
        >
          Capture {poseSteps[currentStep].toUpperCase()}
        </button>

        {measurements.chest && (
          <div className="absolute top-3 left-3 bg-white/90 text-black p-4 rounded-lg shadow-md text-sm w-52 z-30">
            <div className="text-lg font-bold mb-1">📏 Measurements</div>
            <div>👕 Chest: {measurements.chest} cm</div>
            <div>👖 Waist: {measurements.waist} cm</div>
            <div className="mt-1 text-purple-700 font-semibold">
              👗 Suggested Size: {suggestedSize}
            </div>
          </div>
        )}

        {Object.keys(capturedImages).length > 0 && (
          <div className="absolute bottom-20 left-1/2 -translate-x-1/2 flex gap-2 z-20">
            {Object.entries(capturedImages).map(([key, url]) => (
              <img
                key={key}
                src={url}
                alt={`${key} view`}
                className="w-16 h-24 object-cover border-2 border-white rounded"
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Measure;
