import React from 'react';

const PoseOverlay = ({ view }) => {
  let positionStyles = '';
  let verticalGuide = false;

  if (view === 'front' || view === 'back') {
    positionStyles = 'top-[5%] left-[10%] w-[80%] h-[90%]';
    verticalGuide = true;
  } else if (view === 'side') {
    positionStyles = 'top-[5%] left-[15%] w-[70%] h-[90%]';
    verticalGuide = true;
  }

  const instructionText =
    view === 'front'
      ? 'Step 1: Stand facing the camera'
      : view === 'side'
      ? 'Step 2: Turn sideways (left or right)'
      : 'Step 3: Turn around, back to camera';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      {/* Blue silhouette or bounding fill */}
      <div
        className={`absolute ${positionStyles} bg-cyan-500/10 border-4 border-dashed border-purple-500 rounded-xl flex flex-col items-center justify-center animate-pulse-slow`}
      >
        <img
          src={`/images/pose-${view}.png`}
          alt={`${view} pose guide`}
          className="w-28 h-auto opacity-90 mb-2"
        />
        <div className="text-white font-semibold text-sm text-center px-2">
          {instructionText}
        </div>
      </div>

      {/* Optional vertical dashed line to mimic center guide */}
      {verticalGuide && (
        <div className="absolute top-0 bottom-0 left-1/2 border-l-2 border-dashed border-white/50"></div>
      )}
    </div>
  );
};

export default PoseOverlay;
