  const [status, setStatus] = useState("Hold an A4 sheet at chest level and match the bar width.");
  const [barPx, setBarPx] = useState(300);
  const [cmPerPx, setCmPerPx] = useState(null);
 // 'Men' | 'Women' | 'Unisex'

