        };
        setMeas(out);
        setLocked(true);
        setPhase("locked");
        setStatus("Locked âœ”ï¸");
        stopCamera();
      } else {
        setStatus("Hold stillâ€¦");
      }

      ctx.restore();
    });

    startCamera(pose);
    return () => { stopCamera(); };
  } catch (error) {
    console.error("âŒ MediaPipe initialization failed:", error);
    setStatus(`MediaPipe Error: ${error.message}. Please refresh the page.`);
    setPhase("error");
  }
