import React, { useRef, useEffect, useState } from 'react';
import { Pose } from '@mediapipe/pose';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';
import { Camera } from '@mediapipe/camera_utils';

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [userHeight, setUserHeight] = useState('');
  const [landmarks, setLandmarks] = useState(null);
  const [measurements, setMeasurements] = useState(null);

  // Helper: Pixel distance between 2 points
  const getPixelDistance = (point1, point2) => {
    const dx = point1.x - point2.x;
    const dy = point1.y - point2.y;
    return Math.sqrt(dx * dx + dy * dy);
  };

  // Helper: Convert pixel distance to cm using known full height in pixels
  const convertToCm = (pixelLength, fullBodyPixelHeight, realHeightCm) => {
    return (pixelLength / fullBodyPixelHeight) * realHeightCm;
  };

  // Helper: Convert cm to ft/inches
  const convertCmToFeetInches = (cm) => {
    const inches = cm / 2.54;
    const feet = Math.floor(inches / 12);
    const remainingInches = Math.round(inches % 12);
    return `${feet}ft ${remainingInches}in`;
  };

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext('2d');

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      if (results.poseLandmarks) {
        setLandmarks(results.poseLandmarks);
        drawConnectors(canvasCtx, results.poseLandmarks, Pose.POSE_CONNECTIONS, { color: '#00FF00' });
        drawLandmarks(canvasCtx, results.poseLandmarks, { color: '#FF0000', radius: 4 });
      }

      canvasCtx.restore();
    });

    if (videoElement) {
      const camera = new Camera(videoElement, {
        onFrame: async () => {
          await pose.send({ image: videoElement });
        },
        width: 640,
        height: 480,
      });
      camera.start();
    }
  }, []);

  const handleCalculate = () => {
    if (!landmarks || !userHeight) {
      alert("Please enter your height and wait for detection.");
      return;
    }

    const h_cm = parseFloat(userHeight);
    const topHead = landmarks[0];
    const bottomFoot = landmarks[28] || landmarks[32]; // Foot or ankle

    const fullBodyPixelHeight = getPixelDistance(topHead, bottomFoot);

    const leftShoulder = landmarks[11];
    const rightShoulder = landmarks[12];
    const shoulderWidthPx = getPixelDistance(leftShoulder, rightShoulder);
    const shoulderCm = convertToCm(shoulderWidthPx, fullBodyPixelHeight, h_cm);

    const leftHip = landmarks[23];
    const rightHip = landmarks[24];
    const hipWidthPx = getPixelDistance(leftHip, rightHip);
    const hipCm = convertToCm(hipWidthPx, fullBodyPixelHeight, h_cm);

    const torsoLengthPx = getPixelDistance(leftShoulder, leftHip);
    const torsoCm = convertToCm(torsoLengthPx, fullBodyPixelHeight, h_cm);

    setMeasurements({
      shoulder: {
        cm: shoulderCm.toFixed(1),
        ft: convertCmToFeetInches(shoulderCm),
      },
      hip: {
        cm: hipCm.toFixed(1),
        ft: convertCmToFeetInches(hipCm),
      },
      torso: {
        cm: torsoCm.toFixed(1),
        ft: convertCmToFeetInches(torsoCm),
      },
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold text-purple-400 mb-4">AI Body Measurement</h1>

      <div className="flex flex-col md:flex-row gap-6 items-center">
        <div className="relative w-[640px] h-[480px]">
          <video ref={videoRef} className="absolute w-full h-full" autoPlay muted playsInline />
          <canvas ref={canvasRef} width="640" height="480" className="absolute top-0 left-0" />
        </div>

        <div className="flex flex-col gap-4 w-full md:w-80">
          <label className="text-sm text-gray-300">Enter Your Real Height (in cm):</label>
          <input
            type="number"
            value={userHeight}
            onChange={(e) => setUserHeight(e.target.value)}
            className="p-2 rounded bg-gray-700 border border-purple-500 text-white"
            placeholder="e.g. 170"
          />
          <button
            onClick={handleCalculate}
            className="bg-purple-600 hover:bg-purple-700 py-2 px-4 rounded shadow"
          >
            Calculate Measurements
          </button>

          {measurements && (
            <div className="mt-4 space-y-2 text-sm">
              <p>👕 Shoulder Width: {measurements.shoulder.cm} cm ({measurements.shoulder.ft})</p>
              <p>🩳 Hip Width: {measurements.hip.cm} cm ({measurements.hip.ft})</p>
              <p>🧍 Torso Length: {measurements.torso.cm} cm ({measurements.torso.ft})</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Measure;
