import React from 'react';

const PoseOverlay = ({ view }) => {
  // Adjust bounding box and center guide per pose view
  let outerBoxStyles = '';
  let innerBoxStyles = '';
  const showVerticalGuide = true;

  if (view === 'front' || view === 'back') {
    outerBoxStyles = 'top-[5%] left-[10%] w-[80%] h-[90%]';
    innerBoxStyles = 'top-[15%] left-[30%] w-[40%] h-[70%]';
  } else if (view === 'side') {
    outerBoxStyles = 'top-[5%] left-[15%] w-[70%] h-[90%]';
    innerBoxStyles = 'top-[15%] left-[35%] w-[30%] h-[70%]';
  }

  const instructionText =
    view === 'front'
      ? 'Step 1: Stand facing the camera'
      : view === 'side'
      ? 'Step 2: Turn sideways (left or right)'
      : 'Step 3: Turn around, back to camera';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      {/* Outer Green Box */}
      <div className={`absolute ${outerBoxStyles} border-4 border-green-500 rounded-md`} />

      {/* Inner Pink Box */}
      <div className={`absolute ${innerBoxStyles} border-4 border-fuchsia-500`} />

      {/* Vertical dashed center guide */}
      {showVerticalGuide && (
        <div className="absolute top-[5%] h-[90%] left-1/2 border-l-2 border-dashed border-black z-20" />
      )}

      {/* Instruction & icon overlay (centered) */}
      <div className={`absolute ${innerBoxStyles} flex flex-col items-center justify-center`}>
        <img
          src={`/images/pose-${view}.png`}
          alt={`${view} pose guide`}
          className="w-24 h-auto opacity-90 mb-2"
        />
        <div className="text-white font-semibold text-sm text-center px-2 bg-black/40 rounded-md">
          {instructionText}
        </div>
      </div>
    </div>
  );
};

export default PoseOverlay;
