// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";

// --- sizing config ---
const A4_WIDTH_CM = 21.0;          // reference width for calibration
const MULTIPLIER_CHEST = 1.75;     // shoulder flat → chest circumference (~1.6–1.9). Tune with a few tests.

const MEN_SIZE_CHART = [
  { size: "S",   min: 88,  max: 94  },
  { size: "M",   min: 95,  max: 102 },
  { size: "L",   min: 103, max: 110 },
  { size: "XL",  min: 111, max: 118 },
  { size: "2XL", min: 119, max: 126 },
  { size: "3XL", min: 127, max: 134 },
  { size: "4XL", min: 135, max: 145 },
];

function menSizeFromChest(cm) {
  for (const row of MEN_SIZE_CHART) if (cm >= row.min && cm <= row.max) return row.size;
  return cm < MEN_SIZE_CHART[0].min ? "XS/S" : "4XL+";
}

// --- overlay helpers (screenshot style) ---
const UPPER = [
  [0, 1], [1, 2], [2, 3], [3, 7],
  [0, 11], [0, 12],
  [11, 13], [13, 15],
  [12, 14], [14, 16],
  [11, 12],
  [11, 23], [12, 24],
];
const LOWER = [
  [23, 25], [25, 27], [27, 31],
  [24, 26], [26, 28], [28, 32],
  [23, 24],
];

function drawSeg(ctx, lm, a, b, color, width, W, H) {
  const A = lm[a], B = lm[b];
  if (!A || !B) return;
  if ((A.visibility ?? 0) < 0.4 || (B.visibility ?? 0) < 0.4) return;
  ctx.strokeStyle = color;
  ctx.lineWidth = width;
  ctx.beginPath();
  ctx.moveTo(A.x * W, A.y * H);
  ctx.lineTo(B.x * W, B.y * H);
  ctx.stroke();
}

function drawBBox(ctx, lm, W, H, color = "rgba(0,255,0,0.9)") {
  const pts = lm.filter(p => (p?.visibility ?? 0) > 0.4);
  if (!pts.length) return;
  const xs = pts.map(p => p.x * W);
  const ys = pts.map(p => p.y * H);
  const minX = Math.max(0, Math.min(...xs));
  const maxX = Math.min(W, Math.max(...xs));
  const minY = Math.max(0, Math.min(...ys));
  const maxY = Math.min(H, Math.max(...ys));
  const pad = 8;
  ctx.strokeStyle = color;
  ctx.lineWidth = 3;
  ctx.strokeRect(minX - pad, minY - pad, (maxX - minX) + pad * 2, (maxY - minY) + pad * 2);
}

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);

  const [phase, setPhase] = useState("calibrate"); // 'calibrate' | 'measure' | 'locked'
  const [status, setStatus] = useState("Hold an A4 sheet at chest level, then match the bar width.");
  const [cmPerPx, setCmPerPx] = useState(null);
  const [barPx, setBarPx] = useState(300);

  const [locked, setLocked] = useState(false);
  const [result, setResult] = useState(null);

  // stability & framing
  const STABILITY_FRAMES = 12;
  const MOVEMENT_EPS = 0.015;
  const GUIDE_MARGIN = 0.08;
  const recentShoulderN = useRef([]);

  const getDistance = useCallback((a, b) => Math.hypot(a.x - b.x, a.y - b.y), []);

  function stopCamera() {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      v?.srcObject && v.srcObject.getTracks().forEach(t => t.stop());
      if (v) v.srcObject = null;
    } catch {}
  }

  async function startCamera(pose) {
    const videoEl = videoRef.current;
    const camera = new Camera(videoEl, {
      onFrame: async () => { await pose.send({ image: videoEl }); },
      width: 1280,
      height: 720,
    });
    camRef.current = camera;
    camera.start();
  }

  useEffect(() => {
    const canvasEl = canvasRef.current;
    const ctx = canvasEl.getContext("2d");

    const pose = new Pose({
      // ✅ Force the non‑SIMD build to avoid WASM aborts on some browsers
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/pose@0.5.1675469404/${file.replace('simd_', '')}`,
    });
    poseRef.current = pose;

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((res) => {
      if (locked) return;

      const W = res.image.width, H = res.image.height;
      if (canvasEl.width !== W) canvasEl.width = W;
      if (canvasEl.height !== H) canvasEl.height = H;

      const ctx2 = ctx;
      ctx2.save();
      ctx2.clearRect(0, 0, W, H);
      ctx2.drawImage(res.image, 0, 0, W, H);

      // guide box
      const gx = W * GUIDE_MARGIN, gy = H * GUIDE_MARGIN;
      const gW = W * (1 - GUIDE_MARGIN * 2), gH = H * (1 - GUIDE_MARGIN * 2);
      ctx2.strokeStyle = "rgba(0,255,0,0.7)";
      ctx2.lineWidth = 2;
      ctx2.strokeRect(gx, gy, gW, gH);

      // calibration bar (only visible during calibration)
      if (phase === "calibrate") {
        const barH = 14;
        ctx2.fillStyle = "rgba(255,255,255,0.95)";
        ctx2.fillRect((W - barPx) / 2, H * 0.1, barPx, barH);
        ctx2.font = "14px system-ui, sans-serif";
        ctx2.fillStyle = "rgba(255,255,255,0.95)";
        ctx2.fillText("Match this bar to A4 width (21 cm) at chest depth.",
          Math.max(12, (W - 360) / 2), H * 0.1 - 8);
      }

      const lm = res.poseLandmarks;
      if (lm) {
        // overlay like the screenshot
        drawBBox(ctx2, lm, W, H, "rgba(0,255,0,0.9)");
        UPPER.forEach(([a, b]) => drawSeg(ctx2, lm, a, b, "#2aa4ff", 3, W, H));
        LOWER.forEach(([a, b]) => drawSeg(ctx2, lm, a, b, "#ff9933", 3, W, H));

        const vis = (i) => (lm[i]?.visibility ?? 0) > 0.4;
        const Lsh = lm[11], Rsh = lm[12];
        const hip = vis(23) ? lm[23] : lm[24];
        const needed = [11, 12].every(vis) && (vis(23) || vis(24));

        if (!needed) {
          setStatus("Show shoulders & upper torso.");
          ctx2.restore();
          return;
        }

        // draw key dots
        const shMid = { x: (Lsh.x + Rsh.x) / 2, y: (Lsh.y + Rsh.y) / 2 };
        ctx2.fillStyle = "rgba(0,200,255,1)";
        [Lsh, Rsh, shMid, hip].forEach(p => {
          ctx2.beginPath();
          ctx2.arc(p.x * W, p.y * H, 6, 0, Math.PI * 2);
          ctx2.fill();
        });

        // ensure inside guide
        const toPx = (p) => ({ x: p.x * W, y: p.y * H });
        const inGuide = [toPx(Lsh), toPx(Rsh), toPx(hip)].every(
          (p) => p.x >= gx && p.x <= gx + gW && p.y >= gy && p.y <= gy + gH
        );
        if (!inGuide) {
          setStatus("Step back/center yourself inside the frame.");
          ctx2.restore();
          return;
        }

        // Lock & measure only in MEASURE phase
        if (phase === "measure") {
          const shoulderWidthN = getDistance(Lsh, Rsh);
          const torsoHeightN = getDistance(shMid, hip);

          if (torsoHeightN > 0 && cmPerPx) {
            const buf = recentShoulderN.current;
            buf.push(shoulderWidthN);
            if (buf.length > STABILITY_FRAMES) buf.shift();

            const avg = buf.reduce((a, b) => a + b, 0) / buf.length;
            const maxDev = Math.max(...buf.map(v => Math.abs(v - avg)));

            if (buf.length >= STABILITY_FRAMES && maxDev < MOVEMENT_EPS) {
              // compute chest cm from calibrated cm/px
              const Lx = Lsh.x * W, Ly = Lsh.y * H;
              const Rx = Rsh.x * W, Ry = Rsh.y * H;
              const shoulderPx = Math.hypot(Lx - Rx, Ly - Ry);
              const chestFlatCm = shoulderPx * cmPerPx;
              const chestCm = chestFlatCm * MULTIPLIER_CHEST;

              const tshirt = menSizeFromChest(chestCm);
              const fit =
                chestCm <= MEN_SIZE_CHART.find(r => r.size === tshirt)?.max
                  ? "Slim/Regular"
                  : "Regular/Relaxed";

              setResult({
                chestCm: Number(chestCm.toFixed(1)),
                tshirt,
                hoodie: tshirt === "S" ? "S" : tshirt,
                fit,
                debug: {
                  cmPerPx: Number(cmPerPx.toFixed(4)),
                  shoulderPx: Math.round(shoulderPx),
                  shoulderN: Number(shoulderWidthN.toFixed(3)),
                  torsoN: Number(torsoHeightN.toFixed(3)),
                },
              });

              setLocked(true);
              setPhase("locked");
              setStatus("Locked ✔️");
              stopCamera();
            } else {
              setStatus("Hold still for a moment…");
            }
          } else {
            setStatus("Calibrated? If not, click Set calibration.");
          }
        } else if (phase === "calibrate") {
          setStatus("Calibration: adjust bar, then click Set calibration.");
        }
      } else {
        setStatus("Detecting body…");
      }

      ctx2.restore();
    });

    startCamera(pose);

    return () => { stopCamera(); };
  }, [phase, cmPerPx, getDistance, locked, barPx]);

  const handleSetCalibration = () => {
    if (barPx < 40) return;
    setCmPerPx(A4_WIDTH_CM / barPx); // cm per pixel
    setPhase("measure");
    setStatus("Calibration set. Stand centered; shoulders & upper torso visible.");
  };

  const handleRetake = () => {
    setLocked(false);
    setResult(null);
    setPhase("measure");
    setStatus("Stand centered; hold still 1–2 seconds.");
    if (poseRef.current) startCamera(poseRef.current);
  };

  const handleRecalibrate = () => {
    setLocked(false);
    setResult(null);
    setPhase("calibrate");
    setStatus("Hold an A4 sheet at chest level, then match the bar width.");
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="relative w-full min-h-screen bg-black flex flex-col items-center">
      {/* Header */}
      <div className="w-full max-w-5xl flex items-center justify-between px-3 pt-3">
        <div className="text-white/90 font-semibold">Male Upper‑Body Measurement (Real‑time)</div>
        <div className="bg-white/90 rounded px-3 py-1 text-sm">{status}</div>
      </div>

      {/* Camera */}
      <div className="relative w-full max-w-5xl aspect-video mt-3">
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-contain bg-black"
          autoPlay muted playsInline
        />
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full object-contain pointer-events-none"
        />

        {/* Calibration UI */}
        {phase === "calibrate" && (
          <div className="absolute bottom-3 left-1/2 -translate-x-1/2 bg-white/95 rounded-xl p-3 flex items-center gap-3 shadow">
            <div className="text-sm font-medium">A4 width match</div>
            <input
              type="range"
              min={120} max={900}
              value={barPx}
              onChange={(e) => setBarPx(Number(e.target.value))}
              className="w-64"
            />
            <button
              onClick={handleSetCalibration}
              className="px-3 py-2 rounded bg-purple-600 text-white hover:bg-purple-700 text-sm"
            >
              Set calibration
            </button>
          </div>
        )}

        {/* Result modal */}
        {phase === "locked" && result && (
          <div className="absolute inset-0 bg-black/60 flex items-center justify-center p-4">
            <div className="bg-white max-w-md w-full rounded-2xl p-5 shadow-xl">
              <div className="text-xl font-bold mb-2">Recommended Sizes</div>
              <div className="space-y-1">
                <div>Chest: <b>{result.chestCm} cm</b></div>
                <div>👕 T‑Shirt: <b>{result.tshirt}</b></div>
                <div>🧥 Hoodie: <b>{result.hoodie}</b></div>
                <div>🎯 Fit: <b>{result.fit}</b></div>
              </div>
              <div className="mt-3 text-xs text-gray-600">
                Debug — cm/px: {result.debug.cmPerPx}, shoulderPx: {result.debug.shoulderPx},
                shoulderN: {result.debug.shoulderN}, torsoN: {result.debug.torsoN}
              </div>
              <div className="mt-4 flex gap-2">
                <button onClick={handleRetake} className="px-4 py-2 rounded bg-black text-white hover:opacity-90">
                  Retake (keep calibration)
                </button>
                <button onClick={handleRecalibrate} className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300">
                  Re‑calibrate
                </button>
                <button onClick={() => window.print()} className="px-4 py-2 rounded bg-purple-600 text-white hover:bg-purple-700">
                  Save / Print
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="mt-2 text-white/60 text-sm text-center px-3">
        Tip: keep the A4 sheet flat at chest depth during calibration. Stand square to the camera for best accuracy.
      </div>
    </div>
  );
};

export default Measure;
