// src/pages/Measure.jsx
import React, { useRef, useEffect, useState } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const captureCanvasRef = useRef(null); // For snapshot
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      if (results.poseLandmarks) {
        const lm = results.poseLandmarks;
        const xPoints = [lm[0].x, lm[11].x, lm[23].x, lm[27].x].map(x => x * canvasElement.width);
        const yPoints = [lm[0].y, lm[11].y, lm[23].y, lm[27].y].map(y => y * canvasElement.height);

        const minX = Math.min(...xPoints);
        const maxX = Math.max(...xPoints);
        const minY = Math.min(...yPoints);
        const maxY = Math.max(...yPoints);
        const boxWidth = maxX - minX;
        const boxHeight = maxY - minY;

        // Silhouette fill
        canvasCtx.fillStyle = "rgba(0, 255, 255, 0.2)";
        canvasCtx.fillRect(minX, minY, boxWidth, boxHeight);

        // Pink bounding box
        canvasCtx.strokeStyle = "#FF00FF";
        canvasCtx.lineWidth = 2;
        canvasCtx.strokeRect(minX, minY, boxWidth, boxHeight);

        // Green outer box
        canvasCtx.strokeStyle = "#00FF00";
        canvasCtx.strokeRect(40, 40, canvasElement.width - 80, canvasElement.height - 80);

        // Dashed center line
        canvasCtx.setLineDash([5, 5]);
        canvasCtx.beginPath();
        canvasCtx.moveTo(canvasElement.width / 2, 0);
        canvasCtx.lineTo(canvasElement.width / 2, canvasElement.height);
        canvasCtx.strokeStyle = "#000";
        canvasCtx.stroke();
        canvasCtx.setLineDash([]);

        // Pose drawing
        drawConnectors(canvasCtx, lm, Pose.POSE_CONNECTIONS, { color: "#00FF00", lineWidth: 2 });
        drawLandmarks(canvasCtx, lm, { color: "#FF0000", lineWidth: 1 });
      }

      canvasCtx.restore();
    });

    const camera = new Camera(videoRef.current, {
      onFrame: async () => {
        await pose.send({ image: videoRef.current });
      },
      width: 640,
      height: 480,
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, []);

  const handleCapture = () => {
    const captureCanvas = captureCanvasRef.current;
    const drawCanvas = canvasRef.current;
    const ctx = captureCanvas.getContext("2d");

    captureCanvas.width = drawCanvas.width;
    captureCanvas.height = drawCanvas.height;
    ctx.drawImage(drawCanvas, 0, 0);

    const dataUrl = captureCanvas.toDataURL("image/png");
    setImageUrl(dataUrl);
    const a = document.createElement("a");
    a.href = dataUrl;
    a.download = "pose_snapshot.png";
    a.click();
  };

  return (
    <div className="relative w-full h-screen flex flex-col items-center justify-center bg-black">
      {/* Video & canvas */}
      <video ref={videoRef} className="absolute w-full h-full object-cover" autoPlay muted playsInline />
      <canvas ref={canvasRef} className="absolute w-full h-full" />
      <canvas ref={captureCanvasRef} className="hidden" />

      {/* Capture button */}
      <div className="absolute bottom-10 flex gap-4 z-20">
        <button
          onClick={handleCapture}
          className="px-6 py-2 bg-purple-600 text-white rounded-full font-bold hover:bg-purple-800 transition"
        >
          Capture Pose Snapshot
        </button>
      </div>
    </div>
  );
};

export default Measure;
