import React from 'react';
import '../styles/animations.css'; // Make sure this file exists and includes animation if not using Tailwind plugin

const PoseOverlay = ({ view }) => {
  let boxStyles = 'absolute border-4 border-dashed border-purple-500 animate-pulse opacity-60';
  let positionStyles = '';

  if (view === 'front' || view === 'back') {
    positionStyles = 'top-[15%] left-[25%] w-[50%] h-[70%]';
  } else if (view === 'side') {
    positionStyles = 'top-[15%] left-[30%] w-[40%] h-[70%]';
  }

  const instructionText =
    view === 'front'
      ? 'Stand facing camera with arms relaxed'
      : view === 'side'
      ? 'Turn sideways with arms lifted'
      : 'Face backward with posture straight';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      <div className={`${boxStyles} ${positionStyles} flex flex-col items-center justify-center`}>
        <img
          src={`/images/pose-${view}.png`} // Correct path
          alt={`${view} guide`}
          className="w-24 h-auto animate-rotate-slow opacity-70 mb-2"
        />
        <div className="text-white font-semibold text-sm animate-bounce">
          {instructionText}
        </div>
      </div>
    </div>
  );
};

export default PoseOverlay;
