// src/pages/Measure.jsx
import React, { useRef, useEffect } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      const W = canvasElement.width;
      const H = canvasElement.height;

      // Draw Outer Green Box (full margin)
      const outerMargin = 40;
      canvasCtx.strokeStyle = "#00FF00";
      canvasCtx.lineWidth = 3;
      canvasCtx.strokeRect(outerMargin, outerMargin, W - outerMargin * 2, H - outerMargin * 2);

      // Draw Inner Pink Box (central area for silhouette)
      const innerMargin = 120;
      canvasCtx.strokeStyle = "#FF00FF";
      canvasCtx.lineWidth = 2;
      canvasCtx.strokeRect(innerMargin, outerMargin, W - innerMargin * 2, H - outerMargin * 2);

      // Center dashed line
      canvasCtx.beginPath();
      canvasCtx.setLineDash([8, 5]);
      canvasCtx.moveTo(W / 2, 0);
      canvasCtx.lineTo(W / 2, H);
      canvasCtx.strokeStyle = "#000";
      canvasCtx.lineWidth = 2;
      canvasCtx.stroke();
      canvasCtx.setLineDash([]);

      // Semi-transparent fill inside pink box
      canvasCtx.fillStyle = "rgba(0, 255, 255, 0.2)";
      canvasCtx.fillRect(innerMargin, outerMargin, W - innerMargin * 2, H - outerMargin * 2);

      // Pose landmarks
      if (results.poseLandmarks) {
        drawConnectors(canvasCtx, results.poseLandmarks, Pose.POSE_CONNECTIONS, {
          color: "#00FF00",
          lineWidth: 2,
        });
        drawLandmarks(canvasCtx, results.poseLandmarks, {
          color: "#FF0000",
          radius: 5,
        });
      }

      canvasCtx.restore();
    });

    const camera = new Camera(videoRef.current, {
      onFrame: async () => {
        await pose.send({ image: videoRef.current });
      },
      width: 640,
      height: 480,
    });

    camera.start();
    return () => {
      camera.stop();
    };
  }, []);

  return (
    <div className="relative w-full h-screen flex items-center justify-center bg-black">
      <video ref={videoRef} className="absolute w-full h-full object-cover" autoPlay muted playsInline />
      <canvas ref={canvasRef} className="absolute w-full h-full" />
    </div>
  );
};

export default Measure;
