// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { Camera as CameraIcon, Sparkles, Activity, Target } from 'lucide-react';

// Constants - FIXED FOR REALISTIC MEASUREMENTS
const A4_WIDTH_CM = 21.0;
const CHEST_MULTIPLIER = 3.2; // Increased from 1.75 to get realistic chest measurements
const UPPER_WAIST_FACTOR = 1.8; // Increased from 0.90 to get realistic waist measurements

// BALANCED SIZE RECOMMENDATION SYSTEM - All measurements equally important
const recommendSize = (chestCm, shoulderCm, waistCm) => {
  if (!chestCm || !shoulderCm || !waistCm || chestCm <= 0 || shoulderCm <= 0 || waistCm <= 0) {
    return null;
  }

  // REALISTIC SIZE RANGES (in cm) - Based on actual human measurements
  const sizeRanges = {
    XXS: { chest: [70, 80], shoulder: [32, 37], waist: [55, 62] },
    XS:  { chest: [80, 85], shoulder: [37, 40], waist: [62, 67] },
    S:   { chest: [85, 90], shoulder: [40, 43], waist: [67, 72] },
    M:   { chest: [90, 95], shoulder: [43, 46], waist: [72, 77] },
    L:   { chest: [95, 100], shoulder: [46, 49], waist: [77, 82] },
    XL:  { chest: [100, 105], shoulder: [49, 52], waist: [82, 87] },
    XXL: { chest: [105, 110], shoulder: [52, 55], waist: [87, 92] },
    '3XL': { chest: [110, 115], shoulder: [55, 58], waist: [92, 97] },
    '4XL': { chest: [115, 120], shoulder: [58, 61], waist: [97, 102] }
  };

  // Calculate size score for each measurement (0-8)
  const getSizeScore = (value, ranges) => {
    const sizes = ['XXS', 'XS', 'S', 'M', 'L', 'XL', 'XXL', '3XL', '4XL'];
    for (let i = 0; i < sizes.length; i++) {
      const size = sizes[i];
      if (value >= ranges[size][0] && value < ranges[size][1]) {
        return i;
      }
    }
    // Handle values outside ranges
    if (value < ranges.XXS[0]) return 0; // XXS
    return 8; // 4XL
  };

  // Get individual scores - ALL EQUALLY IMPORTANT
  const chestScore = getSizeScore(chestCm, sizeRanges);
  const shoulderScore = getSizeScore(shoulderCm, sizeRanges);
  const waistScore = getSizeScore(waistCm, sizeRanges);

  // BALANCED CALCULATION - All measurements equally important
  const averageScore = (chestScore + shoulderScore + waistScore) / 3;

  // Small body proportion adjustments (much smaller than before)
  let finalScore = averageScore;

  // Waist-to-chest ratio (body shape indicator)
  const waistChestRatio = waistCm / chestCm;
  if (waistChestRatio < 0.65) {
    // Very slim/athletic - go down slightly
    finalScore -= 0.15;
  } else if (waistChestRatio > 0.95) {
    // Fuller build - go up slightly
    finalScore += 0.15;
  }

  // Shoulder-to-chest ratio (shoulder width indicator)
  const shoulderChestRatio = shoulderCm / chestCm;
  if (shoulderChestRatio > 0.55) {
    // Broad shoulders - go up slightly
    finalScore += 0.1;
  } else if (shoulderChestRatio < 0.35) {
    // Narrow shoulders - go down slightly
    finalScore -= 0.1;
  }

  // Convert to size with bounds checking
  const sizeOrder = ['XXS', 'XS', 'S', 'M', 'L', 'XL', 'XXL', '3XL', '4XL'];
  const sizeIndex = Math.max(0, Math.min(sizeOrder.length - 1, Math.round(finalScore)));
  const finalSize = sizeOrder[sizeIndex];

  // Enhanced logging
  console.log(`ðŸŽ¯ BALANCED SIZE CALCULATION:`, {
    measurements: { chestCm: chestCm.toFixed(1), shoulderCm: shoulderCm.toFixed(1), waistCm: waistCm.toFixed(1) },
    scores: { chest: chestScore, shoulder: shoulderScore, waist: waistScore },
    calculation: { averageScore: averageScore.toFixed(2), finalScore: finalScore.toFixed(2), finalSize },
    ratios: { waistChest: waistChestRatio.toFixed(3), shoulderChest: shoulderChestRatio.toFixed(3) }
  });

  return finalSize;
};

// Measurement validation
const validateMeasurements = (chestCm, shoulderCm, waistCm) => {
  const warnings = [];
  const errors = [];

  // Check for unrealistic measurements
  if (chestCm < 60 || chestCm > 150) {
    errors.push(`Chest measurement (${chestCm}cm) is unrealistic. Expected: 60-150cm`);
  } else if (chestCm < 70 || chestCm > 130) {
    warnings.push(`Chest measurement (${chestCm}cm) is outside normal range (70-130cm)`);
  }

  if (shoulderCm < 25 || shoulderCm > 80) {
    errors.push(`Shoulder measurement (${shoulderCm}cm) is unrealistic. Expected: 25-80cm`);
  } else if (shoulderCm < 35 || shoulderCm > 65) {
    warnings.push(`Shoulder measurement (${shoulderCm}cm) is outside normal range (35-65cm)`);
  }

  if (waistCm < 40 || waistCm > 120) {
    errors.push(`Waist measurement (${waistCm}cm) is unrealistic. Expected: 40-120cm`);
  } else if (waistCm < 55 || waistCm > 100) {
    warnings.push(`Waist measurement (${waistCm}cm) is outside normal range (55-100cm)`);
  }

  return { warnings, errors, isValid: errors.length === 0 };
};

// Fit recommendations
const getFitRecommendation = (chestCm, shoulderCm, waistCm) => {
  if (!chestCm || !shoulderCm || !waistCm) return "Standard fit recommended";

  const shoulderChestRatio = shoulderCm / chestCm;
  const waistChestRatio = waistCm / chestCm;

  if (shoulderChestRatio > 0.55 && waistChestRatio < 0.80) {
    return "Athletic fit (broad shoulders, slim waist)";
  } else if (waistChestRatio < 0.75) {
    return "Slim fit (athletic build)";
  } else if (waistChestRatio > 0.90) {
    return "Regular/comfortable fit";
  }

  return "Standard fit";
};

export default function Measure() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);

  const [phase, setPhase] = useState("calibrate");
  const [status, setStatus] = useState("Hold an A4 sheet at chest level and match the bar width.");
  const [barPx, setBarPx] = useState(300);
  const [cmPerPx, setCmPerPx] = useState(null);
  const [locked, setLocked] = useState(false);
  const [meas, setMeas] = useState(null);

  // Stability settings
  const STABILITY_FRAMES = 12;
  const MOVEMENT_EPS = 0.015;
  const recentShoulderN = useRef([]);
  const chestBuf = useRef([]);
  const shoulderBuf = useRef([]);
  const waistBuf = useRef([]);

  const getDistance = useCallback((a, b) => Math.hypot(a.x - b.x, a.y - b.y), []);

  const stopCamera = () => {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      v?.srcObject && v.srcObject.getTracks().forEach(t => t.stop());
      if (v) v.srcObject = null;
    } catch {}
  };

  const startCamera = async (pose) => {
    const v = videoRef.current;
    const camera = new Camera(v, {
      onFrame: async () => { await pose.send({ image: v }); },
      width: 1280, height: 720,
    });
    camRef.current = camera;
    camera.start();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    try {
      const pose = new Pose({
        locateFile: (file) => `/mediapipe/pose/${file.replace('simd_', '')}`,
      });
      poseRef.current = pose;

      pose.setOptions({
        modelComplexity: 0,
        smoothLandmarks: true,
        enableSegmentation: false,
        minDetectionConfidence: 0.6,
        minTrackingConfidence: 0.6,
      });

      pose.onResults((res) => {
        if (locked) return;

        const W = res.image.width, H = res.image.height;
        if (canvas.width !== W) canvas.width = W;
        if (canvas.height !== H) canvas.height = H;

        ctx.save();
        ctx.clearRect(0, 0, W, H);
        ctx.drawImage(res.image, 0, 0, W, H);

        // Guide box
        const gx = W * 0.08, gy = H * 0.08;
        const gW = W * (1 - 0.08 * 2), gH = H * (1 - 0.08 * 2);
        ctx.strokeStyle = "rgba(59, 130, 246, 0.8)";
        ctx.lineWidth = 4;
        ctx.strokeRect(gx, gy, gW, gH);

        // Calibration bar
        if (phase === "calibrate") {
          const barH = 20;
          const barY = H * 0.12;

          ctx.fillStyle = "rgba(59, 130, 246, 0.9)";
          ctx.fillRect((W - barPx) / 2, barY, barPx, barH);

          ctx.strokeStyle = "rgba(255, 255, 255, 0.9)";
          ctx.lineWidth = 2;
          ctx.strokeRect((W - barPx) / 2, barY, barPx, barH);

          ctx.font = "16px system-ui, sans-serif";
          ctx.fillStyle = "rgba(255, 255, 255, 0.95)";
          const text = "Match this bar to A4 width (21 cm) at chest depth";
          const textX = Math.max(20, (W - ctx.measureText(text).width) / 2);
          ctx.fillText(text, textX, barY - 15);
        }

        const lm = res.poseLandmarks;
        if (!lm) { setStatus("Detecting bodyâ€¦"); ctx.restore(); return; }

        // Check visibility
        const vis = (i) => (lm[i]?.visibility ?? 0) > 0.45;
        const shouldersOK = vis(11) && vis(12);
        const hipsOK = vis(23) || vis(24);

        if (!(shouldersOK && hipsOK)) { 
          setStatus("Show shoulders & upper torso."); 
          ctx.restore(); 
          return; 
        }

        const Lsh = lm[11], Rsh = lm[12];
        const Lhip = lm[23], Rhip = lm[24];
        const hip = vis(23) ? Lhip : Rhip;
        const shMid = { x: (Lsh.x + Rsh.x) / 2, y: (Lsh.y + Rsh.y) / 2 };

        // Draw key points
        [Lsh, Rsh, shMid, hip].forEach(point => {
          if (point) {
            ctx.fillStyle = "#3B82F6";
            ctx.beginPath();
            ctx.arc(point.x * W, point.y * H, 8, 0, Math.PI * 2);
            ctx.fill();
          }
        });

        // Check if inside guide
        const inside = [Lsh, Rsh, hip].every(p => 
          p.x * W >= gx && p.x * W <= gx + gW && p.y * H >= gy && p.y * H <= gy + gH
        );
        if (!inside) { 
          setStatus("Step back/center inside the frame."); 
          ctx.restore(); 
          return; 
        }

        if (phase === "calibrate") { 
          setStatus("Adjust bar â†’ Set calibration."); 
          ctx.restore(); 
          return; 
        }
        if (!cmPerPx) { 
          setStatus("Click Set calibration first."); 
          ctx.restore(); 
          return; 
        }

        // Calculate measurements
        const shoulderWidthN = getDistance(Lsh, Rsh);
        const bufN = recentShoulderN.current;
        bufN.push(shoulderWidthN);
        if (bufN.length > STABILITY_FRAMES) bufN.shift();

        const avg = bufN.reduce((a, b) => a + b, 0) / bufN.length;
        const maxDev = Math.max(...bufN.map(v => Math.abs(v - avg)));

        // Measurements in cm
        const shoulderPx = Math.abs((Lsh.x - Rsh.x) * W);
        const shoulderCm = shoulderPx * cmPerPx;
        const chestCmInstant = shoulderCm * CHEST_MULTIPLIER;

        let upperWaistCmInstant = null;
        if (vis(23) && vis(24)) {
          const hipFlatCm = Math.abs((Lhip.x - Rhip.x) * W) * cmPerPx;
          upperWaistCmInstant = hipFlatCm * UPPER_WAIST_FACTOR;
        }

        // Median smoothing
        const pushMed = (ref, val, n = 15) => {
          if (val == null) return null;
          ref.current.push(val);
          if (ref.current.length > n) ref.current.shift();
          const s = [...ref.current].sort((a, b) => a - b);
          const m = Math.floor(s.length / 2);
          return s.length % 2 ? s[m] : (s[m - 1] + s[m]) / 2;
        };

        const chestMed = pushMed(chestBuf, chestCmInstant);
        const shoulderMed = pushMed(shoulderBuf, shoulderCm);
        const waistMed = pushMed(waistBuf, upperWaistCmInstant);

        // Check stability
        const spread = chestBuf.current.length >= 8 ? 
          Math.max(...chestBuf.current) - Math.min(...chestBuf.current) : 999;
        const stable = bufN.length >= STABILITY_FRAMES && maxDev < MOVEMENT_EPS && spread < 1.8;

        if (stable && chestMed && shoulderMed) {
          const out = {
            chestCm: Number(chestMed.toFixed(1)),
            shoulderCm: Number(shoulderMed.toFixed(1)),
            upperWaistCm: waistMed ? Number(waistMed.toFixed(1)) : undefined,
            quality: { frames: chestBuf.current.length, spreadCm: Number(spread.toFixed(2)) }
          };
          setMeas(out);
          setLocked(true);
          setPhase("locked");
          setStatus("Locked âœ”ï¸");
          stopCamera();
        } else {
          setStatus("Hold stillâ€¦");
        }

        ctx.restore();
      });

      startCamera(pose);
      return () => { stopCamera(); };
    } catch (error) {
      console.error("âŒ MediaPipe initialization failed:", error);
      setStatus(`MediaPipe Error: ${error.message}. Please refresh the page.`);
      setPhase("error");
    }
  }, [phase, cmPerPx, getDistance, locked, barPx]);

  const setCalibration = () => {
    if (barPx < 40) return;
    setCmPerPx(A4_WIDTH_CM / barPx);
    setPhase("measure");
    setStatus("Calibration set. Stand centered; shoulders & upper torso visible.");
    chestBuf.current = []; 
    shoulderBuf.current = []; 
    waistBuf.current = []; 
    recentShoulderN.current = [];
  };

  const retake = () => {
    setLocked(false); 
    setMeas(null); 
    setPhase("measure");
    setStatus("Stand centered; hold still 1â€“2 seconds.");
    chestBuf.current = []; 
    shoulderBuf.current = []; 
    waistBuf.current = []; 
    recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };

  const recalibrate = () => {
    setLocked(false); 
    setMeas(null); 
    setPhase("calibrate");
    setStatus("Hold an A4 sheet at chest level and match the bar width.");
    chestBuf.current = []; 
    shoulderBuf.current = []; 
    waistBuf.current = []; 
    recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-indigo-900 relative overflow-hidden">
      {/* Background */}
      <div className="absolute inset-0">
        <div className="absolute top-10 left-10 w-72 h-72 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-10 right-10 w-96 h-96 bg-emerald-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
      </div>

      <div className="relative z-10 flex flex-col items-center min-h-screen">
        {/* Header */}
        <div className="w-full max-w-7xl flex items-center justify-between px-6 py-6">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-emerald-500 rounded-2xl flex items-center justify-center shadow-lg">
              <Sparkles className="w-7 h-7 text-white" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-white">AI Fashion Fit</h1>
              <p className="text-sm text-blue-200">Balanced Body Measurement System</p>
            </div>
          </div>

          {/* Status Badge */}
          <div className="bg-gradient-to-r from-blue-500 to-emerald-500 rounded-full px-6 py-3 shadow-lg">
            <div className="flex items-center space-x-3 text-white">
              {phase === "locked" ? <span className="text-xl">âœ…</span> : 
               phase === "measure" ? <Activity className="w-5 h-5 animate-pulse" /> : 
               <Target className="w-5 h-5" />}
              <span className="font-medium text-sm">{status}</span>
            </div>
          </div>
        </div>

        {/* Camera Section */}
        <div className="w-full max-w-6xl px-6 flex-1 flex items-center">
          <div className="relative w-full bg-black/20 backdrop-blur-lg rounded-3xl overflow-hidden shadow-2xl border border-white/10">
            <div className="relative aspect-video">
              <video ref={videoRef} className="absolute inset-0 w-full h-full object-contain bg-black rounded-3xl" autoPlay muted playsInline />
              <canvas ref={canvasRef} className="absolute inset-0 w-full h-full object-contain pointer-events-none" />

              {/* Calibration UI */}
              {phase === "calibrate" && (
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2">
                  <div className="bg-white/95 backdrop-blur-md rounded-2xl px-6 py-4 shadow-2xl border border-white/20">
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-2">
                        <Target className="w-5 h-5 text-amber-600" />
                        <span className="text-sm font-medium text-gray-700">A4 width calibration</span>
                      </div>
                      <input 
                        type="range" 
                        min={120} 
                        max={900} 
                        value={barPx} 
                        onChange={(e) => setBarPx(Number(e.target.value))} 
                        className="w-64 accent-blue-500"
                      />
                      <button 
                        onClick={setCalibration} 
                        className="bg-gradient-to-r from-blue-500 to-emerald-500 hover:from-blue-600 hover:to-emerald-600 text-white px-6 py-2 rounded-xl font-medium transition-all duration-300 transform hover:scale-105 shadow-lg"
                      >
                        Set Calibration
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Instructions */}
        <div className="w-full max-w-4xl px-6 py-4">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 border border-white/20">
            <div className="text-center text-white">
              <h3 className="font-semibold mb-3">ðŸ“ Measurement Instructions</h3>
              <div className="text-sm text-white/80 space-y-2">
                {phase === "calibrate" && (
                  <p>ðŸ“„ Hold an A4 sheet at chest level and adjust the bar to match its width for accurate calibration.</p>
                )}
                {phase === "measure" && (
                  <p>ðŸ§ Stand straight with arms slightly away from your body. Hold still for precise measurement.</p>
                )}
                {phase === "locked" && (
                  <p>âœ… Measurements captured successfully! You can retake if needed.</p>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Results Display */}
        {phase === "locked" && meas && (
          <div className="w-full max-w-4xl px-6 py-6">
            <div className="bg-white/95 backdrop-blur-md rounded-2xl p-6 border border-gray-200 shadow-xl">
              <div className="text-center text-gray-800">
                <h3 className="text-xl font-bold mb-4">ðŸ“Š Upper Body Measurement Results</h3>

                {/* Measurements Grid */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                  <div className="bg-blue-50 rounded-xl p-4 border border-blue-200">
                    <div className="text-2xl font-bold text-blue-600">{meas.chestCm?.toFixed(1) || 'N/A'}</div>
                    <div className="text-sm text-blue-500">Chest (cm)</div>
                  </div>
                  <div className="bg-emerald-50 rounded-xl p-4 border border-emerald-200">
                    <div className="text-2xl font-bold text-emerald-600">{meas.shoulderCm?.toFixed(1) || 'N/A'}</div>
                    <div className="text-sm text-emerald-500">Shoulder (cm)</div>
                  </div>
                  <div className="bg-purple-50 rounded-xl p-4 border border-purple-200">
                    <div className="text-2xl font-bold text-purple-600">{meas.upperWaistCm?.toFixed(1) || 'N/A'}</div>
                    <div className="text-sm text-purple-500">Waist (cm)</div>
                  </div>
                </div>

                {/* Size Recommendation */}
                <div className="bg-gradient-to-r from-blue-50 to-emerald-50 rounded-xl p-4 mb-6 border border-blue-200">
                  <div className="text-lg font-semibold text-gray-700 mb-2">ðŸŽ¯ Recommended Size</div>
                  <div className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-emerald-600 bg-clip-text text-transparent mb-2">
                    {recommendSize(meas.chestCm, meas.upperWaistCm, meas.shoulderCm)}
                  </div>
                  <div className="text-sm text-gray-600 mb-3">
                    Based on <strong>all three measurements equally</strong> - not just chest!
                  </div>

                  {/* How it works */}
                  <div className="bg-white/50 rounded-lg p-3 border border-blue-200">
                    <h5 className="text-sm font-medium text-blue-800 mb-2">ðŸ”¬ How Size is Calculated:</h5>
                    <div className="text-xs text-blue-700 space-y-1">
                      <p>â€¢ <strong>Step 1:</strong> Each measurement gets a size score (0-8)</p>
                      <p>â€¢ <strong>Step 2:</strong> Calculate average of all three scores</p>
                      <p>â€¢ <strong>Step 3:</strong> Apply small adjustments for body proportions</p>
                      <p>â€¢ <strong>Step 4:</strong> Final size = balanced average, not chest-dominant</p>
                    </div>
                  </div>
                </div>

                {/* Fit Recommendations */}
                {meas.chestCm && meas.shoulderCm && meas.upperWaistCm && (
                  <div className="bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl p-4 mb-6 border border-purple-200">
                    <h4 className="font-semibold text-purple-800 mb-2">ðŸ’¡ Fit Recommendations</h4>
                    <div className="text-sm text-purple-700">
                      <p><strong>Fit Type:</strong> {getFitRecommendation(meas.chestCm, meas.shoulderCm, meas.upperWaistCm)}</p>
                    </div>
                  </div>
                )}

                {/* Measurement Validation */}
                {meas.chestCm && meas.shoulderCm && meas.upperWaistCm && (() => {
                  const validation = validateMeasurements(meas.chestCm, meas.shoulderCm, meas.upperWaistCm);
                  if (validation.errors.length > 0 || validation.warnings.length > 0) {
                    return (
                      <div className="bg-yellow-50 rounded-xl p-4 mb-6 border border-yellow-200">
                        <h4 className="font-semibold text-yellow-800 mb-2">âš ï¸ Measurement Validation</h4>
                        {validation.errors.length > 0 && (
                          <div className="mb-3">
                            <p className="text-sm font-medium text-red-700 mb-1">âŒ Critical Issues:</p>
                            <ul className="text-xs text-red-600 space-y-1">
                              {validation.errors.map((error, index) => (
                                <li key={index}>â€¢ {error}</li>
                              ))}
                            </ul>
                          </div>
                        )}
                        {validation.warnings.length > 0 && (
                          <div>
                            <p className="text-sm font-medium text-yellow-700 mb-1">âš ï¸ Warnings:</p>
                            <ul className="text-xs text-yellow-600 space-y-1">
                              {validation.warnings.map((warning, index) => (
                                <li key={index}>â€¢ {warning}</li>
                              ))}
                            </ul>
                          </div>
                        )}
                        <div className="mt-3 p-2 bg-blue-50 rounded border border-blue-200">
                          <p className="text-xs text-blue-700">
                            <strong>ðŸ’¡ Tip:</strong> If measurements seem incorrect, try recalibrating with the A4 sheet.
                          </p>
                        </div>
                      </div>
                    );
                  }
                  return null;
                })()}

                {/* Action Buttons */}
                <div className="flex flex-col sm:flex-row gap-3 justify-center">
                  <button 
                    onClick={retake}
                    className="bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-3 rounded-xl font-medium transition-all duration-300 border-2 border-gray-300"
                  >
                    ðŸ”„ Retake
                  </button>
                  <button 
                    onClick={recalibrate}
                    className="bg-blue-100 hover:bg-blue-200 text-blue-700 px-6 py-3 rounded-xl font-medium transition-all duration-300 border-2 border-blue-300"
                  >
                    ðŸŽ¯ Recalibrate
                  </button>
                  <button 
                    onClick={() => {
                      const recommendedSize = recommendSize(meas.chestCm, meas.upperWaistCm, meas.shoulderCm);
                      if (recommendedSize) {
                        const measurementData = encodeURIComponent(JSON.stringify({
                          size: recommendedSize,
                          measurements: {
                            chestCm: meas.chestCm,
                            shoulderCm: meas.shoulderCm,
                            upperWaistCm: meas.upperWaistCm
                          }
                        }));
                        window.location.href = `/recommendations?data=${measurementData}`;
                      }
                    }}
                    className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-6 py-3 rounded-xl font-medium transition-all duration-300 shadow-lg"
                  >
                    âœ¨ Get Recommendations
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
