import React from 'react';

const PoseOverlay = ({ view }) => {
  let positionStyles = '';

  if (view === 'front' || view === 'back') {
    positionStyles = 'top-[5%] left-[10%] w-[80%] h-[90%]';
  } else if (view === 'side') {
    positionStyles = 'top-[5%] left-[15%] w-[70%] h-[90%]';
  }

  const instructionText =
    view === 'front'
      ? 'Step 1: Stand facing the camera'
      : view === 'side'
      ? 'Step 2: Turn sideways (left or right)'
      : 'Step 3: Turn around, back to camera';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10 backdrop-blur-sm">
      <div
        className={`absolute ${positionStyles} border-4 border-dashed border-purple-500 rounded-xl bg-black/20 flex flex-col items-center justify-center animate-pulse-slow`}
      >
        <img
          src={`/images/pose-${view}.png`}
          alt={`${view} pose guide`}
          onError={(e) => (e.target.style.display = 'none')}
          className="w-28 h-auto opacity-90 mb-2"
        />
        <div className="text-white font-semibold text-sm text-center px-2">
          {instructionText}
        </div>
      </div>
    </div>
  );
};

export default PoseOverlay;
