import React from 'react';

const PoseOverlay = ({ view, isFinal }) => {
  let boxStyles = 'absolute border-4 border-dashed border-purple-500 animate-pulse opacity-60';
  let positionStyles = '';

  if (view === 'front' || view === 'back') {
    positionStyles = 'top-[15%] left-[25%] w-[50%] h-[70%]';
  } else if (view === 'side') {
    positionStyles = 'top-[15%] left-[30%] w-[40%] h-[70%]';
  }

  const instructionText = isFinal
    ? '‚úÖ Measurement complete!'
    : view === 'front'
    ? 'üßç‚Äç‚ôÇÔ∏è Face the camera with arms relaxed'
    : view === 'side'
    ? '‚Ü™Ô∏è Turn sideways with arms lifted'
    : 'üîô Face backward with posture straight';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      <div className={`${boxStyles} ${positionStyles} flex flex-col items-center justify-center`}>
        {!isFinal && (
          <img
            src={`/images/pose-${view}.png`}
            alt={`${view} guide`}
            className="w-24 h-auto animate-rotate-slow opacity-70 mb-2"
          />
        )}
        <div className="text-white font-semibold text-sm animate-bounce text-center px-2">
          {instructionText}
        </div>
      </div>
    </div>
  );
};

export default PoseOverlay;
