// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";

const A4_WIDTH_CM = 21.0;         // reference width for calibration
const MULTIPLIER_CHEST = 1.75;    // shoulder flat width â†’ chest circumference (~1.6â€“1.9). Tune with 2â€“3 test users.

const MEN_SIZE_CHART = [
  { size: "S",   min: 88,  max: 94  },
  { size: "M",   min: 95,  max: 102 },
  { size: "L",   min: 103, max: 110 },
  { size: "XL",  min: 111, max: 118 },
  { size: "2XL", min: 119, max: 126 },
  { size: "3XL", min: 127, max: 134 },
  { size: "4XL", min: 135, max: 145 }, // extend if needed
];

function menSizeFromChest(cm) {
  for (const row of MEN_SIZE_CHART) {
    if (cm >= row.min && cm <= row.max) return row.size;
  }
  return cm < MEN_SIZE_CHART[0].min ? "XS/S" : "4XL+";
}

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);

  const [phase, setPhase] = useState("calibrate"); // 'calibrate' | 'measure' | 'locked'
  const [status, setStatus] = useState("Hold an A4 sheet at chest level, then match the bar width.");
  const [cmPerPx, setCmPerPx] = useState(null);   // set after calibration
  const [barPx, setBarPx] = useState(300);        // on-screen calibration bar width in pixels

  const [locked, setLocked] = useState(false);
  const [result, setResult] = useState(null);     // { chestCm, tshirt, fit, debug }

  // measurement stability settings
  const STABILITY_FRAMES = 12;
  const MOVEMENT_EPS = 0.015;
  const GUIDE_MARGIN = 0.08;

  const getDistance = useCallback((a, b) => Math.hypot(a.x - b.x, a.y - b.y), []);

  function stopCamera() {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      const s = v?.srcObject;
      if (s) s.getTracks().forEach((t) => t.stop());
      if (v) v.srcObject = null;
    } catch {}
  }

  async function startCamera(pose) {
    const videoEl = videoRef.current;
    const camera = new Camera(videoEl, {
      onFrame: async () => { await pose.send({ image: videoEl }); },
      width: 1280,
      height: 720,
    });
    camRef.current = camera;
    camera.start();
  }

  useEffect(() => {
    const canvasEl = canvasRef.current;
    const ctx = canvasEl.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });
    poseRef.current = pose;

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    const recentShoulderN = [];

    pose.onResults((res) => {
      const W = res.image.width, H = res.image.height;
      if (canvasEl.width !== W) canvasEl.width = W;
      if (canvasEl.height !== H) canvasEl.height = H;

      ctx.save();
      ctx.clearRect(0, 0, W, H);
      ctx.drawImage(res.image, 0, 0, W, H);

      // guide box
      const gx = W * GUIDE_MARGIN, gy = H * GUIDE_MARGIN;
      const gW = W * (1 - GUIDE_MARGIN * 2), gH = H * (1 - GUIDE_MARGIN * 2);
      ctx.strokeStyle = "rgba(0,255,0,0.8)";
      ctx.lineWidth = 3;
      ctx.strokeRect(gx, gy, gW, gH);

      // calibration bar (only in calibration phase)
      if (phase === "calibrate") {
        const barH = 14;
        ctx.fillStyle = "rgba(255,255,255,0.9)";
        ctx.fillRect((W - barPx) / 2, H * 0.1, barPx, barH);
        ctx.font = "14px sans-serif";
        ctx.fillStyle = "rgba(255,255,255,0.9)";
        ctx.fillText("Match this bar to the A4 paper width (21 cm) you hold at chest level.",
          (W - Math.min(barPx + 220, W - 20)) / 2, H * 0.1 - 8);
      }

      if (phase !== "measure") {
        ctx.restore();
        return;
      }

      const lm = res.poseLandmarks;
      if (!lm) {
        setStatus("Detecting shouldersâ€¦");
        ctx.restore();
        return;
      }

      const vis = (i) => (lm[i]?.visibility ?? 0) > 0.5;
      const shouldersOK = vis(11) && vis(12);
      const hipsOK = vis(23) || vis(24);

      if (!(shouldersOK && hipsOK)) {
        setStatus("Show both shoulders and upper torso.");
        ctx.restore();
        return;
      }

      const Lsh = lm[11], Rsh = lm[12];
      const hip = vis(23) ? lm[23] : lm[24];

      // inside guide
      const toPx = (p) => ({ x: p.x * W, y: p.y * H });
      const pts = [Lsh, Rsh, hip].map(toPx);
      const insideGuide = pts.every(p => p.x >= gx && p.x <= gx + gW && p.y >= gy && p.y <= gy + gH);
      if (!insideGuide) {
        setStatus("Step back/center yourself inside the frame.");
        ctx.restore();
        return;
      }

      // normalized measures
      const shoulderWidthN = getDistance(Lsh, Rsh);  // 0..1
      const torsoHeightN = getDistance({ x: (Lsh.x + Rsh.x)/2, y: (Lsh.y + Rsh.y)/2 }, hip);

      if (torsoHeightN <= 0) {
        setStatus("Hold stillâ€¦");
        ctx.restore();
        return;
      }

      // stability buffer
      recentShoulderN.push(shoulderWidthN);
      if (recentShoulderN.length > STABILITY_FRAMES) recentShoulderN.shift();

      if (recentShoulderN.length >= STABILITY_FRAMES) {
        const avg = recentShoulderN.reduce((a, b) => a + b, 0) / recentShoulderN.length;
        const maxDev = Math.max(...recentShoulderN.map(v => Math.abs(v - avg)));

        if (maxDev < MOVEMENT_EPS && cmPerPx) {
          // compute chest in cm from calibrated cm/px
          const LshP = toPx(Lsh), RshP = toPx(Rsh);
          const shoulderPx = Math.hypot(LshP.x - RshP.x, LshP.y - RshP.y);
          const chestFlatCm = shoulderPx * cmPerPx;
          const chestCm = chestFlatCm * MULTIPLIER_CHEST;

          const tshirt = menSizeFromChest(chestCm);
          const fit = chestCm <= MEN_SIZE_CHART.find(r => r.size === tshirt)?.max ? "Slim/Regular" : "Regular/Relaxed";

          setResult({
            chestCm: Number(chestCm.toFixed(1)),
            tshirt,
            hoodie: tshirt === "S" ? "S" : tshirt, // adjust if you want size-up for hoodies
            fit,
            debug: {
              cmPerPx: Number(cmPerPx.toFixed(4)),
              shoulderPx: Math.round(shoulderPx),
              shoulderN: Number(shoulderWidthN.toFixed(3)),
              torsoN: Number(torsoHeightN.toFixed(3)),
            }
          });

          setLocked(true);
          setPhase("locked");
          setStatus("Locked âœ”ï¸");
          stopCamera();
        } else {
          setStatus("Hold still for a momentâ€¦");
        }
      } else {
        setStatus("Framing looks good â€” keep stillâ€¦");
      }

      // minimal dots
      ctx.fillStyle = "rgba(0,200,255,0.9)";
      pts.forEach(p => { ctx.beginPath(); ctx.arc(p.x, p.y, 6, 0, Math.PI*2); ctx.fill(); });

      ctx.restore();
    });

    startCamera(pose);

    return () => { stopCamera(); };
  }, [phase, cmPerPx, getDistance, barPx]);

  const handleSetCalibration = () => {
    // cm per pixel = 21 cm / (bar width in px on your screen)
    if (barPx < 40) return;
    const val = A4_WIDTH_CM / barPx;
    setCmPerPx(val);
    setPhase("measure");
    setStatus("Calibration set. Stand centered; shoulders & upper torso visible.");
  };

  const handleRetake = () => {
    setPhase("measure");         // keep calibration; just reâ€‘measure
    setLocked(false);
    setResult(null);
    setStatus("Stand centered; hold still 1â€“2 seconds.");
    if (poseRef.current) startCamera(poseRef.current);
  };

  const handleRecalibrate = () => {
    setPhase("calibrate");
    setLocked(false);
    setResult(null);
    setStatus("Hold an A4 sheet at chest level, then match the bar width.");
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="relative w-full min-h-screen bg-black flex flex-col items-center">
      {/* Header */}
      <div className="w-full max-w-5xl flex items-center justify-between px-3 pt-3">
        <div className="text-white/90 font-semibold">Male Upperâ€‘Body Measurement (Realâ€‘time)</div>
        <div className="bg-white/90 rounded px-3 py-1 text-sm">{status}</div>
      </div>

      {/* Camera area */}
      <div className="relative w-full max-w-5xl aspect-video mt-3">
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-contain bg-black"
          autoPlay muted playsInline
        />
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full object-contain pointer-events-none"
        />

        {/* Calibration panel overlay */}
        {phase === "calibrate" && (
          <div className="absolute bottom-3 left-1/2 -translate-x-1/2 bg-white/95 rounded-xl p-3 flex items-center gap-3 shadow">
            <div className="text-sm font-medium">A4 width match:</div>
            <input
              type="range"
              min={120}
              max={900}
              value={barPx}
              onChange={(e) => setBarPx(Number(e.target.value))}
              className="w-64"
            />
            <button
              onClick={handleSetCalibration}
              className="px-3 py-2 rounded bg-purple-600 text-white hover:bg-purple-700 text-sm"
            >
              Set calibration
            </button>
          </div>
        )}

        {/* Result modal */}
        {phase === "locked" && result && (
          <div className="absolute inset-0 bg-black/60 flex items-center justify-center p-4">
            <div className="bg-white max-w-md w-full rounded-2xl p-5 shadow-xl">
              <div className="text-xl font-bold mb-2">Recommended Sizes</div>
              <div className="space-y-1">
                <div>Chest: <b>{result.chestCm} cm</b></div>
                <div>ðŸ‘• Tâ€‘Shirt: <b>{result.tshirt}</b></div>
                <div>ðŸ§¥ Hoodie: <b>{result.hoodie}</b></div>
                <div>ðŸŽ¯ Fit: <b>{result.fit}</b></div>
              </div>

              <div className="mt-3 text-xs text-gray-600">
                Debug â€” cm/px: {result.debug.cmPerPx}, shoulderPx: {result.debug.shoulderPx},
                shoulderN: {result.debug.shoulderN}, torsoN: {result.debug.torsoN}
              </div>

              <div className="mt-4 flex gap-2">
                <button onClick={handleRetake} className="px-4 py-2 rounded bg-black text-white hover:opacity-90">
                  Retake (keep calibration)
                </button>
                <button onClick={handleRecalibrate} className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300">
                  Reâ€‘calibrate
                </button>
                <button onClick={() => window.print()} className="px-4 py-2 rounded bg-purple-600 text-white hover:bg-purple-700">
                  Save / Print
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="mt-2 text-white/60 text-sm text-center px-3">
        Tip: keep the A4 sheet flat at chest depth during calibration. For best accuracy, stand square to the camera.
      </div>
    </div>
  );
};

export default Measure;
