import React, { useState, useEffect } from 'react';
import { 
  Sparkles, 
  TrendingUp, 
  Heart, 
  ShoppingBag, 
  Star, 
  Filter, 
  Search,
  ArrowRight,
  Zap,
  Target,
  Users,
  Award,
  Clock,
  MapPin,
  CheckCircle
} from 'lucide-react';
import ClothingRecommendation from '../components/ClothingRecommendation';

// Mock user preferences and style data
const USER_PREFERENCES = {
  style: "smart-casual",
  budget: "medium",
  occasion: "work",
  colors: ["navy", "gray", "white"],
  brands: ["Zara", "H&M", "Uniqlo"]
};

// Style inspiration data
const STYLE_INSPIRATIONS = [
  {
    id: 1,
    name: "Smart Casual Office",
    description: "Professional yet comfortable for modern workplaces",
    image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=300&fit=crop",
    tags: ["office", "smart-casual", "professional"],
    rating: 4.8,
    saves: 1247
  },
  {
    id: 2,
    name: "Weekend Street Style",
    description: "Trendy and comfortable for casual outings",
    image: "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=400&h=300&fit=crop",
    tags: ["casual", "streetwear", "trendy"],
    rating: 4.6,
    saves: 892
  },
  {
    id: 3,
    name: "Minimalist Wardrobe",
    description: "Clean, simple pieces that work together",
    image: "https://images.unsplash.com/photo-1445205170230-053b83016050?w=400&h=300&fit=crop",
    tags: ["minimalist", "versatile", "timeless"],
    rating: 4.9,
    saves: 2156
  }
];

// Seasonal recommendations
const SEASONAL_RECOMMENDATIONS = {
  spring: [
    { name: "Light Denim Jacket", category: "outerwear", price: "$80-120" },
    { name: "Linen Shirts", category: "tops", price: "$55-75" },
    { name: "Chino Shorts", category: "bottoms", price: "$40-60" }
  ],
  summer: [
    { name: "Cotton Polo Shirts", category: "tops", price: "$35-50" },
    { name: "Linen Pants", category: "bottoms", price: "$60-80" },
    { name: "Lightweight Blazer", category: "outerwear", price: "$120-180" }
  ],
  autumn: [
    { name: "Wool Sweaters", category: "tops", price: "$70-100" },
    { name: "Corduroy Pants", category: "bottoms", price: "$65-85" },
    { name: "Trench Coat", category: "outerwear", price: "$150-250" }
  ],
  winter: [
    { name: "Merino Wool Sweaters", category: "tops", price: "$80-120" },
    { name: "Wool Blazer", category: "outerwear", price: "$180-280" },
    { name: "Warm Scarf", category: "accessories", price: "$30-50" }
  ]
};

const Recommendations = () => {
  const [activeTab, setActiveTab] = useState('recommendations');
  const [selectedSeason, setSelectedSeason] = useState('spring');
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState([]);

  // Mock measurements - in real app, this would come from the measurement system
  const mockMeasurements = {
    chestCm: 98,
    shoulderCm: 45,
    upperWaistCm: 82
  };

  const toggleFavorite = (item) => {
    setFavorites(prev => 
      prev.find(fav => fav.id === item.id)
        ? prev.filter(fav => fav.id !== item.id)
        : [...prev, item]
    );
  };

  const isFavorite = (item) => {
    return favorites.some(fav => fav.id === item.id);
  };

  const getCurrentSeason = () => {
    const month = new Date().getMonth();
    if (month >= 2 && month <= 4) return 'spring';
    if (month >= 5 && month <= 7) return 'summer';
    if (month >= 8 && month <= 10) return 'autumn';
    return 'winter';
  };

  useEffect(() => {
    setSelectedSeason(getCurrentSeason());
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-indigo-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-500 rounded-2xl flex items-center justify-center shadow-lg">
                <Sparkles className="w-7 h-7 text-white" />
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-800">Style Recommendations</h1>
                <p className="text-gray-600">AI-powered fashion advice based on your measurements</p>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              <div className="bg-gradient-to-r from-green-500 to-emerald-500 text-white px-4 py-2 rounded-full text-sm font-medium">
                Measurements: {mockMeasurements.chestCm}cm chest
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6">
          <nav className="flex space-x-8">
            {[
              { id: 'recommendations', label: 'AI Recommendations', icon: Sparkles },
              { id: 'inspiration', label: 'Style Inspiration', icon: Heart },
              { id: 'seasonal', label: 'Seasonal Picks', icon: TrendingUp },
              { id: 'wardrobe', label: 'Wardrobe Builder', icon: ShoppingBag }
            ].map((tab) => {
              const Icon = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === tab.id
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <div className="flex items-center space-x-2">
                    <Icon className="w-4 h-4" />
                    <span>{tab.label}</span>
                  </div>
                </button>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        {activeTab === 'recommendations' && (
          <div className="space-y-8">
            {/* AI Recommendations */}
            <ClothingRecommendation 
              measurements={mockMeasurements}
              onItemSelect={(item) => console.log('Selected item:', item)}
            />

            {/* Personal Style Insights */}
            <div className="bg-white rounded-2xl shadow-xl p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center space-x-3">
                <Target className="w-6 h-6 text-blue-600" />
                <span>Personal Style Insights</span>
              </h2>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-4 border border-blue-100">
                  <div className="flex items-center space-x-3 mb-3">
                    <div className="w-10 h-10 bg-blue-500 rounded-lg flex items-center justify-center">
                      <TrendingUp className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-800">Body Type</h3>
                      <p className="text-sm text-gray-600">Athletic Build</p>
                    </div>
                  </div>
                  <p className="text-sm text-gray-700">
                    Your measurements suggest an athletic build. Consider fitted styles that highlight your proportions.
                  </p>
                </div>

                <div className="bg-gradient-to-br from-purple-50 to-pink-50 rounded-xl p-4 border border-purple-100">
                  <div className="flex items-center space-x-3 mb-3">
                    <div className="w-10 h-10 bg-purple-500 rounded-lg flex items-center justify-center">
                      <Star className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-800">Style Preference</h3>
                      <p className="text-sm text-gray-600">Smart Casual</p>
                    </div>
                  </div>
                  <p className="text-sm text-gray-700">
                    Based on your preferences, smart casual styles will work best for your lifestyle.
                  </p>
                </div>

                <div className="bg-gradient-to-br from-green-50 to-emerald-50 rounded-xl p-4 border border-green-100">
                  <div className="flex items-center space-x-3 mb-3">
                    <div className="w-10 h-10 bg-green-500 rounded-lg flex items-center justify-center">
                      <Zap className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-800">Fit Recommendation</h3>
                      <p className="text-sm text-gray-600">Regular Fit</p>
                    </div>
                  </div>
                  <p className="text-sm text-gray-700">
                    Regular fits will provide the best balance of comfort and style for your measurements.
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'inspiration' && (
          <div className="space-y-8">
            <div className="bg-white rounded-2xl shadow-xl p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Style Inspiration</h2>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {STYLE_INSPIRATIONS.map((style) => (
                  <div key={style.id} className="bg-gray-50 rounded-xl overflow-hidden hover:shadow-lg transition-shadow">
                    <div className="relative">
                      <img 
                        src={style.image} 
                        alt={style.name}
                        className="w-full h-48 object-cover"
                      />
                      <button
                        onClick={() => toggleFavorite(style)}
                        className={`absolute top-3 right-3 p-2 rounded-full transition-colors ${
                          isFavorite(style) 
                            ? 'bg-red-500 text-white' 
                            : 'bg-white/80 text-gray-600 hover:bg-red-500 hover:text-white'
                        }`}
                      >
                        <Heart className={`w-4 h-4 ${isFavorite(style) ? 'fill-current' : ''}`} />
                      </button>
                    </div>

                    <div className="p-4">
                      <h3 className="font-semibold text-gray-800 mb-2">{style.name}</h3>
                      <p className="text-sm text-gray-600 mb-3">{style.description}</p>

                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center space-x-1">
                          <Star className="w-4 h-4 text-yellow-500 fill-current" />
                          <span className="text-sm font-medium">{style.rating}</span>
                        </div>
                        <div className="flex items-center space-x-1 text-sm text-gray-500">
                          <Heart className="w-4 h-4" />
                          <span>{style.saves}</span>
                        </div>
                      </div>

                      <div className="flex flex-wrap gap-2 mb-3">
                        {style.tags.map((tag, index) => (
                          <span key={index} className="px-2 py-1 bg-blue-100 text-blue-700 rounded-full text-xs">
                            {tag}
                          </span>
                        ))}
                      </div>

                      <button className="w-full bg-gradient-to-r from-blue-500 to-purple-500 text-white py-2 px-4 rounded-lg font-medium hover:from-blue-600 hover:to-purple-600 transition-all duration-300">
                        View Outfit
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'seasonal' && (
          <div className="space-y-8">
            <div className="bg-white rounded-2xl shadow-xl p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Seasonal Recommendations</h2>

              {/* Season Selector */}
              <div className="flex space-x-2 mb-6">
                {Object.keys(SEASONAL_RECOMMENDATIONS).map((season) => (
                  <button
                    key={season}
                    onClick={() => setSelectedSeason(season)}
                    className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                      selectedSeason === season
                        ? 'bg-blue-500 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {season.charAt(0).toUpperCase() + season.slice(1)}
                  </button>
                ))}
              </div>

              {/* Seasonal Items */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {SEASONAL_RECOMMENDATIONS[selectedSeason].map((item, index) => (
                  <div key={index} className="bg-gradient-to-br from-gray-50 to-blue-50 rounded-xl p-4 border border-gray-200">
                    <div className="flex items-center justify-between mb-3">
                      <h3 className="font-semibold text-gray-800">{item.name}</h3>
                      <span className="text-sm text-gray-500 capitalize">{item.category}</span>
                    </div>
                    <p className="text-lg font-bold text-blue-600 mb-3">{item.price}</p>
                    <button className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg font-medium hover:bg-blue-600 transition-colors">
                      Shop Now
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'wardrobe' && (
          <div className="space-y-8">
            <div className="bg-white rounded-2xl shadow-xl p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Wardrobe Builder</h2>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {/* Essential Items */}
                <div>
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Essential Items</h3>
                  <div className="space-y-3">
                    {[
                      "White T-shirt (3-5 pieces)",
                      "Blue Jeans (2-3 pairs)",
                      "White Button-down Shirt",
                      "Navy Blazer",
                      "Black Dress Shoes",
                      "Brown Belt",
                      "Watch"
                    ].map((item, index) => (
                      <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                        <CheckCircle className="w-5 h-5 text-green-500" />
                        <span className="text-gray-700">{item}</span>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Color Palette */}
                <div>
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Recommended Color Palette</h3>
                  <div className="grid grid-cols-2 gap-3">
                    {[
                      { name: "Navy", hex: "#1e3a8a", description: "Versatile, professional" },
                      { name: "Gray", hex: "#6b7280", description: "Neutral, sophisticated" },
                      { name: "White", hex: "#ffffff", description: "Clean, crisp" },
                      { name: "Olive", hex: "#84cc16", description: "Earth tone, casual" }
                    ].map((color, index) => (
                      <div key={index} className="p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center space-x-3 mb-2">
                          <div 
                            className="w-6 h-6 rounded-full border border-gray-300"
                            style={{ backgroundColor: color.hex }}
                          />
                          <span className="font-medium text-gray-800">{color.name}</span>
                        </div>
                        <p className="text-xs text-gray-600">{color.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
