import React, { useState, useRef, useCallback } from 'react';
import { Camera, Upload, Ruler, CheckCircle, ArrowRight, RotateCcw, Save } from 'lucide-react';

const UpperBodyMeasurement = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [capturedImage, setCapturedImage] = useState(null);
  const [measurements, setMeasurements] = useState({});
  const [isProcessing, setIsProcessing] = useState(false);
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Required measurements for upper body garments
  const measurementPoints = [
    {
      id: 'chest',
      label: 'Chest/Bust',
      description: 'Around the fullest part of chest, under arms',
      importance: 'Critical for t-shirt and shirt fit',
      icon: '📏'
    },
    {
      id: 'shoulder',
      label: 'Shoulder Width',
      description: 'From shoulder point to shoulder point',
      importance: 'Determines shirt shoulder seam placement',
      icon: '📐'
    },
    {
      id: 'arm_length',
      label: 'Arm Length',
      description: 'From shoulder to wrist',
      importance: 'For long sleeve shirts and jackets',
      icon: '📏'
    },
    {
      id: 'neck',
      label: 'Neck Circumference',
      description: 'Around the base of neck',
      importance: 'For collar sizing in dress shirts',
      icon: '⭕'
    },
    {
      id: 'torso_length',
      label: 'Torso Length',
      description: 'From shoulder to waist',
      importance: 'Determines shirt length and fit',
      icon: '📏'
    },
    {
      id: 'bicep',
      label: 'Bicep Circumference',
      description: 'Around the largest part of upper arm',
      importance: 'For sleeve fit in fitted shirts',
      icon: '💪'
    }
  ];

  const steps = [
    'Position & Capture',
    'AI Analysis',
    'Measurement Results',
    'Size Recommendations'
  ];

  // Camera capture functionality
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 1280, 
          height: 720,
          facingMode: 'user' 
        } 
      });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error('Error accessing camera:', err);
      alert('Camera access denied. Please use file upload instead.');
    }
  };

  const capturePhoto = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0);

    const imageData = canvas.toDataURL('image/jpeg');
    setCapturedImage(imageData);

    // Stop camera stream
    const stream = video.srcObject;
    const tracks = stream.getTracks();
    tracks.forEach(track => track.stop());

    processImage(imageData);
  };

  // File upload functionality
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCapturedImage(e.target.result);
        processImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Simulate AI processing
  const processImage = async (imageData) => {
    setIsProcessing(true);
    setCurrentStep(1);

    // Simulate AI processing time
    await new Promise(resolve => setTimeout(resolve, 3000));

    // Simulate AI measurement results
    const simulatedMeasurements = {
      chest: Math.floor(Math.random() * 10) + 36, // 36-46 inches
      shoulder: Math.floor(Math.random() * 4) + 16, // 16-20 inches
      arm_length: Math.floor(Math.random() * 4) + 24, // 24-28 inches
      neck: Math.floor(Math.random() * 3) + 14, // 14-17 inches
      torso_length: Math.floor(Math.random() * 4) + 24, // 24-28 inches
      bicep: Math.floor(Math.random() * 4) + 12 // 12-16 inches
    };

    setMeasurements(simulatedMeasurements);
    setIsProcessing(false);
    setCurrentStep(2);
  };

  const resetMeasurement = () => {
    setCurrentStep(0);
    setCapturedImage(null);
    setMeasurements({});
    setIsProcessing(false);
  };

  const generateRecommendations = () => {
    setCurrentStep(3);
  };

  // Size calculation based on measurements
  const calculateSize = () => {
    const chest = measurements.chest || 0;
    if (chest <= 36) return 'XS';
    if (chest <= 38) return 'S';
    if (chest <= 40) return 'M';
    if (chest <= 42) return 'L';
    if (chest <= 44) return 'XL';
    return 'XXL';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-white to-pink-50 p-6">
      <div className="max-w-4xl mx-auto">

        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-purple-700 to-pink-600 bg-clip-text text-transparent mb-4">
            AI Upper Body Measurement
          </h1>
          <p className="text-gray-600 text-lg">
            Precise measurements for perfect t-shirt and shirt fitting
          </p>
        </div>

        {/* Progress Steps */}
        <div className="flex justify-center mb-8">
          <div className="flex items-center space-x-4">
            {steps.map((step, index) => (
              <div key={index} className="flex items-center">
                <div className={`flex items-center justify-center w-10 h-10 rounded-full ${
                  index <= currentStep 
                    ? 'bg-gradient-to-r from-purple-600 to-pink-600 text-white' 
                    : 'bg-gray-200 text-gray-500'
                }`}>
                  {index < currentStep ? <CheckCircle className="w-5 h-5" /> : index + 1}
                </div>
                <span className={`ml-2 text-sm font-medium ${
                  index <= currentStep ? 'text-purple-600' : 'text-gray-400'
                }`}>
                  {step}
                </span>
                {index < steps.length - 1 && (
                  <ArrowRight className="w-4 h-4 text-gray-300 ml-4" />
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Main Content */}
        <div className="bg-white rounded-2xl shadow-xl p-8">

          {/* Step 0: Position & Capture */}
          {currentStep === 0 && (
            <div className="text-center">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">
                Position Instructions for Accurate Measurement
              </h2>

              {/* Instructions */}
              <div className="grid md:grid-cols-2 gap-6 mb-8">
                <div className="bg-purple-50 p-6 rounded-xl">
                  <h3 className="font-bold text-purple-700 mb-4">📋 Preparation Guidelines</h3>
                  <ul className="text-left space-y-2 text-gray-700">
                    <li>• Wear fitted clothing or undergarments</li>
                    <li>• Stand against a plain background</li>
                    <li>• Ensure good lighting</li>
                    <li>• Remove bulky clothing/jewelry</li>
                    <li>• Stand straight with arms at sides</li>
                  </ul>
                </div>

                <div className="bg-pink-50 p-6 rounded-xl">
                  <h3 className="font-bold text-pink-700 mb-4">📐 Measurement Points</h3>
                  <ul className="text-left space-y-2 text-gray-700">
                    <li>• {measurementPoints[0].icon} Chest circumference</li>
                    <li>• {measurementPoints[1].icon} Shoulder width</li>
                    <li>• {measurementPoints[2].icon} Arm length</li>
                    <li>• {measurementPoints[3].icon} Neck circumference</li>
                    <li>• {measurementPoints[4].icon} Torso length</li>
                  </ul>
                </div>
              </div>

              {/* Camera/Upload Options */}
              {!capturedImage && (
                <div className="space-y-4">
                  <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <button
                      onClick={startCamera}
                      className="bg-gradient-to-r from-purple-600 to-pink-600 text-white px-8 py-4 rounded-xl font-semibold flex items-center justify-center space-x-3 hover:shadow-lg transition-all duration-300"
                    >
                      <Camera className="w-5 h-5" />
                      <span>Use Camera</span>
                    </button>

                    <button
                      onClick={() => fileInputRef.current?.click()}
                      className="bg-white border-2 border-purple-200 text-purple-600 px-8 py-4 rounded-xl font-semibold flex items-center justify-center space-x-3 hover:bg-purple-50 transition-all duration-300"
                    >
                      <Upload className="w-5 h-5" />
                      <span>Upload Photo</span>
                    </button>
                  </div>

                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </div>
              )}

              {/* Video Preview */}
              <div className="mt-6">
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  className="mx-auto rounded-xl max-w-md w-full"
                  style={{ display: capturedImage ? 'none' : 'block' }}
                />
                <canvas ref={canvasRef} className="hidden" />

                {videoRef.current?.srcObject && !capturedImage && (
                  <button
                    onClick={capturePhoto}
                    className="mt-4 bg-gradient-to-r from-green-500 to-blue-500 text-white px-6 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300"
                  >
                    📸 Capture Photo
                  </button>
                )}
              </div>
            </div>
          )}

          {/* Step 1: AI Analysis */}
          {currentStep === 1 && (
            <div className="text-center py-12">
              {capturedImage && (
                <img src={capturedImage} alt="Captured" className="mx-auto rounded-xl max-w-md w-full mb-6" />
              )}

              <div className="space-y-4">
                <div className="animate-spin w-12 h-12 border-4 border-purple-200 border-t-purple-600 rounded-full mx-auto"></div>
                <h2 className="text-2xl font-bold text-gray-800">AI Processing Your Image</h2>
                <p className="text-gray-600">
                  Our advanced AI is analyzing your body measurements...
                </p>
                <div className="bg-purple-50 p-4 rounded-xl max-w-md mx-auto">
                  <p className="text-sm text-purple-700">
                    ✨ Detecting key measurement points<br/>
                    📐 Calculating precise dimensions<br/>
                    🎯 Ensuring accuracy for perfect fit
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Measurement Results */}
          {currentStep === 2 && Object.keys(measurements).length > 0 && (
            <div>
              <div className="text-center mb-8">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Your Measurements</h2>
                <p className="text-gray-600">AI-generated precise measurements for upper body garments</p>
              </div>

              <div className="grid md:grid-cols-2 gap-6 mb-8">
                {capturedImage && (
                  <div className="text-center">
                    <img src={capturedImage} alt="Analyzed" className="w-full rounded-xl max-w-sm mx-auto" />
                  </div>
                )}

                <div className="space-y-4">
                  {measurementPoints.map((point) => (
                    <div key={point.id} className="bg-gray-50 p-4 rounded-xl">
                      <div className="flex justify-between items-center mb-2">
                        <span className="font-semibold text-gray-800">{point.icon} {point.label}</span>
                        <span className="text-2xl font-bold text-purple-600">
                          {measurements[point.id]}"
                        </span>
                      </div>
                      <p className="text-sm text-gray-600">{point.description}</p>
                      <p className="text-xs text-purple-600 mt-1">{point.importance}</p>
                    </div>
                  ))}
                </div>
              </div>

              <div className="flex justify-center space-x-4">
                <button
                  onClick={resetMeasurement}
                  className="bg-gray-200 text-gray-700 px-6 py-3 rounded-xl font-semibold flex items-center space-x-2 hover:bg-gray-300 transition-all duration-300"
                >
                  <RotateCcw className="w-5 h-5" />
                  <span>Retake</span>
                </button>

                <button
                  onClick={generateRecommendations}
                  className="bg-gradient-to-r from-purple-600 to-pink-600 text-white px-8 py-3 rounded-xl font-semibold flex items-center space-x-2 hover:shadow-lg transition-all duration-300"
                >
                  <span>Get Size Recommendations</span>
                  <ArrowRight className="w-5 h-5" />
                </button>
              </div>
            </div>
          )}

          {/* Step 3: Size Recommendations */}
          {currentStep === 3 && (
            <div>
              <div className="text-center mb-8">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Size Recommendations</h2>
                <p className="text-gray-600">Based on your measurements, here are your ideal sizes</p>
              </div>

              <div className="grid md:grid-cols-3 gap-6 mb-8">
                <div className="bg-gradient-to-br from-purple-50 to-pink-50 p-6 rounded-xl text-center border-2 border-purple-200">
                  <h3 className="font-bold text-purple-700 mb-2">👕 T-Shirts</h3>
                  <div className="text-3xl font-bold text-purple-600 mb-2">{calculateSize()}</div>
                  <p className="text-sm text-gray-600">Perfect casual fit</p>
                </div>

                <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-6 rounded-xl text-center border-2 border-blue-200">
                  <h3 className="font-bold text-blue-700 mb-2">👔 Dress Shirts</h3>
                  <div className="text-3xl font-bold text-blue-600 mb-2">{calculateSize()}</div>
                  <p className="text-sm text-gray-600">Collar: {measurements.neck}"</p>
                </div>

                <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-6 rounded-xl text-center border-2 border-green-200">
                  <h3 className="font-bold text-green-700 mb-2">🧥 Jackets</h3>
                  <div className="text-3xl font-bold text-green-600 mb-2">{calculateSize()}</div>
                  <p className="text-sm text-gray-600">Regular fit</p>
                </div>
              </div>

              <div className="bg-yellow-50 p-6 rounded-xl mb-6">
                <h3 className="font-bold text-yellow-800 mb-4">📋 Detailed Size Guide</h3>
                <div className="grid md:grid-cols-2 gap-4 text-sm">
                  <div>
                    <p><strong>Chest:</strong> {measurements.chest}" - Size {calculateSize()}</p>
                    <p><strong>Shoulder:</strong> {measurements.shoulder}" - Perfect fit</p>
                    <p><strong>Arm Length:</strong> {measurements.arm_length}" - Regular sleeves</p>
                  </div>
                  <div>
                    <p><strong>Neck:</strong> {measurements.neck}" - Comfortable collar</p>
                    <p><strong>Torso:</strong> {measurements.torso_length}" - Regular length</p>
                    <p><strong>Bicep:</strong> {measurements.bicep}" - Fitted sleeves</p>
                  </div>
                </div>
              </div>

              <div className="flex justify-center space-x-4">
                <button
                  onClick={resetMeasurement}
                  className="bg-gray-200 text-gray-700 px-6 py-3 rounded-xl font-semibold flex items-center space-x-2 hover:bg-gray-300 transition-all duration-300"
                >
                  <RotateCcw className="w-5 h-5" />
                  <span>New Measurement</span>
                </button>

                <button className="bg-gradient-to-r from-green-500 to-blue-500 text-white px-8 py-3 rounded-xl font-semibold flex items-center space-x-2 hover:shadow-lg transition-all duration-300">
                  <Save className="w-5 h-5" />
                  <span>Save Results</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UpperBodyMeasurement;
