import React, { useState, useEffect } from 'react';
import { Sparkles, Search, Filter, Star, ShoppingBag, Heart, ArrowRight, CheckCircle, AlertCircle } from 'lucide-react';
import ApiService from '../services/api';

const Recommendations = () => {
  const [selectedSize, setSelectedSize] = useState('M');
  const [gender, setGender] = useState('Men');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [availableSizes, setAvailableSizes] = useState([]);
  const [statistics, setStatistics] = useState(null);

  // Available sizes for selection
  const sizes = ['XXS', 'XS', 'S', 'M', 'L', 'XL', 'XXL', 'Free Size'];

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    if (selectedSize) {
      loadRecommendations();
    }
  }, [selectedSize, gender]);

  const loadInitialData = async () => {
    try {
      setLoading(true);

      // Load available sizes and statistics
      const [sizesData, statsData] = await Promise.all([
        ApiService.getAvailableSizes(),
        ApiService.getStatistics()
      ]);

      setAvailableSizes(sizesData.available_sizes || sizes);
      setStatistics(statsData);

    } catch (error) {
      console.error('Error loading initial data:', error);
      setError('Failed to load initial data');
    } finally {
      setLoading(false);
    }
  };

  const loadRecommendations = async () => {
    try {
      setLoading(true);
      setError(null);

      const data = await ApiService.getRecommendations(selectedSize, gender, 12);
      setRecommendations(data.recommendations || []);

    } catch (error) {
      console.error('Error loading recommendations:', error);
      setError('Failed to load recommendations');
      setRecommendations([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      setLoading(true);
      setError(null);

      const data = await ApiService.searchProducts(searchQuery, selectedSize, 20);
      setRecommendations(data.results || []);

    } catch (error) {
      console.error('Error searching products:', error);
      setError('Search failed');
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return '/placeholder-clothing.jpg';

    // Convert relative path to absolute URL for ML images
    if (imagePath.startsWith('data/images/')) {
      // Use the backend API to proxy the image from ML API
      return `http://localhost:5002/api/ml/image/${imagePath.replace('data/images/', '')}`;
    }

    return imagePath;
  };

  const getConfidenceColor = (score) => {
    if (score >= 0.8) return 'text-green-600';
    if (score >= 0.6) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getConfidenceText = (score) => {
    if (score >= 0.8) return 'High';
    if (score >= 0.6) return 'Medium';
    return 'Low';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-emerald-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 flex items-center">
                <Sparkles className="w-8 h-8 text-purple-600 mr-3" />
                AI Clothing Recommendations
              </h1>
              <p className="text-gray-600 mt-2">
                Discover perfect upper-body clothing based on your size and preferences
              </p>
            </div>

            {statistics && (
              <div className="text-right">
                <div className="text-2xl font-bold text-blue-600">
                  {statistics.total_upper_body_products?.toLocaleString() || '0'}
                </div>
                <div className="text-sm text-gray-500">Products Available</div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Controls */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white rounded-xl shadow-sm border p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            {/* Size Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Select Size
              </label>
              <select
                value={selectedSize}
                onChange={(e) => setSelectedSize(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {sizes.map(size => (
                  <option key={size} value={size}>{size}</option>
                ))}
              </select>
            </div>

            {/* Gender Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Gender
              </label>
              <select
                value={gender}
                onChange={(e) => setGender(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="Men">Men</option>
                <option value="Women">Women</option>
                <option value="Unisex">Unisex</option>
              </select>
            </div>

            {/* Search */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search Products
              </label>
              <div className="flex">
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="e.g., blue shirt, casual t-shirt"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-l-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                />
                <button
                  onClick={handleSearch}
                  className="px-4 py-2 bg-blue-600 text-white rounded-r-lg hover:bg-blue-700 focus:ring-2 focus:ring-blue-500"
                >
                  <Search className="w-4 h-4" />
                </button>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="flex flex-wrap gap-2">
            <button
              onClick={loadRecommendations}
              className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 flex items-center"
            >
              <Sparkles className="w-4 h-4 mr-2" />
              Get Recommendations
            </button>

            <button
              onClick={() => setSearchQuery('')}
              className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
            >
              Clear Search
            </button>
          </div>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="text-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="text-gray-600 mt-4">Loading recommendations...</p>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <div className="flex items-center">
              <AlertCircle className="w-5 h-5 text-red-600 mr-2" />
              <span className="text-red-800">{error}</span>
            </div>
          </div>
        )}

        {/* Recommendations Grid */}
        {!loading && !error && recommendations.length > 0 && (
          <div className="mb-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold text-gray-900">
                Recommendations for Size {selectedSize}
              </h2>
              <span className="text-gray-600">
                {recommendations.length} products found
              </span>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {recommendations.map((item, index) => (
                <div key={`${item.id}-${index}`} className="bg-white rounded-xl shadow-sm border overflow-hidden hover:shadow-lg transition-shadow">
                  {/* Product Image */}
                  <div className="aspect-square bg-gray-100 relative">
                    {item.image_exists ? (
                      <img
                        src={getImageUrl(item.image_path)}
                        alt={item.product_name}
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.src = '/placeholder-clothing.jpg';
                        }}
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-gray-400">
                        <ShoppingBag className="w-12 h-12" />
                      </div>
                    )}

                    {/* Confidence Badge */}
                    <div className="absolute top-2 right-2">
                      <span className={`px-2 py-1 text-xs font-medium rounded-full bg-white shadow-sm ${getConfidenceColor(item.confidence_score)}`}>
                        {getConfidenceText(item.confidence_score)} Confidence
                      </span>
                    </div>
                  </div>

                  {/* Product Info */}
                  <div className="p-4">
                    <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2">
                      {item.product_name}
                    </h3>

                    <div className="space-y-2 text-sm text-gray-600">
                      <div className="flex items-center justify-between">
                        <span>Type:</span>
                        <span className="font-medium capitalize">{item.article_type}</span>
                      </div>

                      <div className="flex items-center justify-between">
                        <span>Color:</span>
                        <span className="font-medium capitalize">{item.color}</span>
                      </div>

                      <div className="flex items-center justify-between">
                        <span>Usage:</span>
                        <span className="font-medium capitalize">{item.usage}</span>
                      </div>

                      <div className="flex items-center justify-between">
                        <span>Season:</span>
                        <span className="font-medium capitalize">{item.season}</span>
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="mt-4 flex items-center justify-between">
                      <button className="flex items-center text-blue-600 hover:text-blue-700 text-sm font-medium">
                        View Details
                        <ArrowRight className="w-4 h-4 ml-1" />
                      </button>

                      <div className="flex items-center space-x-2">
                        <button className="p-2 text-gray-400 hover:text-red-500 transition-colors">
                          <Heart className="w-4 h-4" />
                        </button>
                        <button className="p-2 text-gray-400 hover:text-blue-500 transition-colors">
                          <ShoppingBag className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* No Results */}
        {!loading && !error && recommendations.length === 0 && (
          <div className="text-center py-12">
            <ShoppingBag className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No recommendations found</h3>
            <p className="text-gray-600">
              Try adjusting your size selection or search criteria
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
