import React from 'react';
import Navbar from '../components/Navbar';

const Home = () => {
  return (
    <>
      <Navbar />
      <section className="text-center py-16 px-6 bg-gray-50">
        <h1 className="text-4xl md:text-5xl font-bold text-purple-700 mb-4">
          Smarter Fitting. Styled by AI.
        </h1>
        <p className="text-lg text-gray-600 mb-6">
          Upload your photo. Let our AI measure and recommend your perfect outfit.
        </p>
        <a
          href="/login"
          className="inline-block bg-purple-600 text-white px-6 py-3 rounded-full hover:bg-purple-700 transition"
        >
          Get Your Fit
        </a>
      </section>
    </>
  );
};

export default Home;
