import React from 'react';

const Home = () => {
  return (
    <section className="min-h-screen bg-gradient-to-b from-gray-100 via-white to-gray-100 flex items-center justify-center px-4">
      <div className="bg-white/60 backdrop-blur-md p-10 rounded-xl shadow-2xl max-w-xl w-full text-center">
        <h1 className="text-4xl md:text-5xl font-extrabold text-purple-700 mb-4">
          Smarter Fitting. Styled by AI.
        </h1>
        <p className="text-base md:text-lg text-gray-700 mb-6">
          Upload your photo. Let our AI measure and recommend your perfect outfit.
        </p>
        <a
          href="/login"
          className="inline-block bg-purple-600 text-white px-6 py-3 rounded-full hover:bg-purple-700 transition-transform transform hover:scale-105 shadow-md"
        >
          Get Your Fit
        </a>
      </div>
    </section>
  );
};

export default Home;
