import React, { useState, useEffect } from 'react';
import { 
  Shirt, 
  Tshirt, 
  Jacket, 
  Dress, 
  Pants, 
  Heart, 
  Star, 
  TrendingUp, 
  Zap, 
  CheckCircle,
  ShoppingBag,
  Filter,
  Search
} from 'lucide-react';

// Clothing database with styles, fits, and recommendations
const CLOTHING_DATABASE = {
  tops: {
    tshirts: [
      { name: "Classic Crew Neck", style: "casual", fit: "regular", sizes: ["XS", "S", "M", "L", "XL"], colors: ["white", "black", "navy", "gray"], price: "$25-35" },
      { name: "V-Neck Tee", style: "casual", fit: "slim", sizes: ["XS", "S", "M", "L", "XL"], colors: ["white", "black", "navy"], price: "$20-30" },
      { name: "Oversized Tee", style: "streetwear", fit: "loose", sizes: ["S", "M", "L", "XL"], colors: ["black", "white", "olive"], price: "$30-40" },
      { name: "Polo Shirt", style: "smart-casual", fit: "regular", sizes: ["XS", "S", "M", "L", "XL"], colors: ["navy", "white", "red", "gray"], price: "$35-50" }
    ],
    shirts: [
      { name: "Oxford Button-Down", style: "smart-casual", fit: "regular", sizes: ["XS", "S", "M", "L", "XL"], colors: ["white", "blue", "pink"], price: "$45-65" },
      { name: "Linen Shirt", style: "casual", fit: "relaxed", sizes: ["S", "M", "L", "XL"], colors: ["beige", "white", "light-blue"], price: "$55-75" },
      { name: "Dress Shirt", style: "formal", fit: "slim", sizes: ["XS", "S", "M", "L", "XL"], colors: ["white", "blue", "black"], price: "$60-90" }
    ],
    jackets: [
      { name: "Denim Jacket", style: "casual", fit: "regular", sizes: ["XS", "S", "M", "L", "XL"], colors: ["blue", "black", "white"], price: "$80-120" },
      { name: "Bomber Jacket", style: "streetwear", fit: "regular", sizes: ["S", "M", "L", "XL"], colors: ["black", "olive", "navy"], price: "$100-150" },
      { name: "Blazer", style: "formal", fit: "slim", sizes: ["S", "M", "L", "XL"], colors: ["navy", "black", "gray"], price: "$150-250" }
    ]
  },
  bottoms: {
    jeans: [
      { name: "Slim Fit Jeans", style: "casual", fit: "slim", sizes: ["28", "30", "32", "34", "36"], colors: ["blue", "black", "gray"], price: "$60-90" },
      { name: "Regular Fit Jeans", style: "casual", fit: "regular", sizes: ["28", "30", "32", "34", "36"], colors: ["blue", "black", "gray"], price: "$50-80" },
      { name: "Relaxed Fit Jeans", style: "casual", fit: "loose", sizes: ["30", "32", "34", "36", "38"], colors: ["blue", "black"], price: "$55-85" }
    ],
    pants: [
      { name: "Chino Pants", style: "smart-casual", fit: "regular", sizes: ["28", "30", "32", "34", "36"], colors: ["khaki", "navy", "gray"], price: "$45-75" },
      { name: "Cargo Pants", style: "casual", fit: "loose", sizes: ["S", "M", "L", "XL"], colors: ["olive", "black", "navy"], price: "$50-80" },
      { name: "Dress Pants", style: "formal", fit: "slim", sizes: ["28", "30", "32", "34", "36"], colors: ["black", "navy", "gray"], price: "$70-110" }
    ]
  }
};

// Style recommendation logic based on body proportions
const getStyleRecommendations = (measurements) => {
  const { chestCm, shoulderCm, upperWaistCm } = measurements;

  let recommendations = {
    fit: "regular",
    style: "versatile",
    notes: []
  };

  // Fit recommendations based on chest-to-shoulder ratio
  if (chestCm && shoulderCm) {
    const ratio = chestCm / shoulderCm;
    if (ratio < 3.6) {
      recommendations.fit = "loose";
      recommendations.notes.push("Your proportions suggest comfortable loose fits");
    } else if (ratio > 4.6) {
      recommendations.fit = "slim";
      recommendations.notes.push("Slim or tailored fits will complement your build");
    } else {
      recommendations.fit = "regular";
      recommendations.notes.push("Regular fits will be most comfortable");
    }
  }

  // Style recommendations based on measurements
  if (chestCm > 110) {
    recommendations.style = "relaxed";
    recommendations.notes.push("Consider relaxed, comfortable styles");
  } else if (chestCm < 90) {
    recommendations.style = "fitted";
    recommendations.notes.push("Fitted styles will enhance your silhouette");
  }

  return recommendations;
};

// Filter clothing based on recommendations
const filterClothing = (clothing, recommendations, searchTerm = "") => {
  return Object.entries(clothing).map(([category, items]) => {
    const filteredItems = items.filter(item => {
      // Filter by fit preference
      if (recommendations.fit && item.fit !== recommendations.fit) {
        return false;
      }

      // Filter by search term
      if (searchTerm && !item.name.toLowerCase().includes(searchTerm.toLowerCase())) {
        return false;
      }

      return true;
    });

    return {
      category,
      items: filteredItems
    };
  }).filter(category => category.items.length > 0);
};

const ClothingRecommendation = ({ measurements, onItemSelect }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [favorites, setFavorites] = useState([]);

  const styleRecs = getStyleRecommendations(measurements);
  const filteredClothing = filterClothing(CLOTHING_DATABASE, styleRecs, searchTerm);

  const toggleFavorite = (item) => {
    setFavorites(prev => 
      prev.find(fav => fav.name === item.name && fav.category === item.category)
        ? prev.filter(fav => !(fav.name === item.name && fav.category === item.category))
        : [...prev, { ...item, category: item.category }]
    );
  };

  const isFavorite = (item) => {
    return favorites.some(fav => fav.name === item.name && fav.category === item.category);
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'tshirts': return <Tshirt className="w-5 h-5" />;
      case 'shirts': return <Shirt className="w-5 h-5" />;
      case 'jackets': return <Jacket className="w-5 h-5" />;
      case 'jeans': return <Pants className="w-5 h-5" />;
      case 'pants': return <Pants className="w-5 h-5" />;
      default: return <Shirt className="w-5 h-5" />;
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-xl p-6">
      {/* Header */}
      <div className="mb-6">
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-500 rounded-xl flex items-center justify-center">
            <Zap className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-gray-800">AI Style Recommendations</h2>
            <p className="text-gray-600">Based on your measurements</p>
          </div>
        </div>

        {/* Style Analysis */}
        <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-4 border border-blue-100">
          <div className="flex items-center space-x-3 mb-3">
            <TrendingUp className="w-5 h-5 text-blue-600" />
            <span className="font-semibold text-gray-800">Style Analysis</span>
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <div className="text-sm text-gray-600">Recommended Fit</div>
              <div className="font-semibold text-blue-600 capitalize">{styleRecs.fit}</div>
            </div>
            <div>
              <div className="text-sm text-gray-600">Style Preference</div>
              <div className="font-semibold text-purple-600 capitalize">{styleRecs.style}</div>
            </div>
          </div>
          <div className="mt-3 text-sm text-gray-700">
            {styleRecs.notes.map((note, index) => (
              <div key={index} className="flex items-center space-x-2">
                <CheckCircle className="w-4 h-4 text-green-500" />
                <span>{note}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Search and Filter */}
      <div className="mb-6">
        <div className="flex space-x-3">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search clothing items..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="all">All Categories</option>
            <option value="tops">Tops</option>
            <option value="bottoms">Bottoms</option>
          </select>
        </div>
      </div>

      {/* Clothing Recommendations */}
      <div className="space-y-6">
        {filteredClothing.map((category) => (
          <div key={category.category} className="space-y-4">
            <h3 className="text-lg font-semibold text-gray-800 capitalize flex items-center space-x-2">
              {getCategoryIcon(category.category)}
              <span>{category.category.replace(/([A-Z])/g, ' $1').trim()}</span>
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {category.items.map((item, index) => (
                <div key={index} className="bg-gray-50 rounded-xl p-4 border border-gray-200 hover:border-blue-300 transition-all duration-300 hover:shadow-md">
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-800 mb-1">{item.name}</h4>
                      <div className="flex items-center space-x-2 mb-2">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          item.fit === 'slim' ? 'bg-blue-100 text-blue-700' :
                          item.fit === 'loose' ? 'bg-green-100 text-green-700' :
                          'bg-gray-100 text-gray-700'
                        }`}>
                          {item.fit} fit
                        </span>
                        <span className="px-2 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-700">
                          {item.style}
                        </span>
                      </div>
                    </div>
                    <button
                      onClick={() => toggleFavorite(item)}
                      className={`p-2 rounded-lg transition-colors ${
                        isFavorite(item) 
                          ? 'text-red-500 hover:text-red-600' 
                          : 'text-gray-400 hover:text-red-500'
                      }`}
                    >
                      <Heart className={`w-5 h-5 ${isFavorite(item) ? 'fill-current' : ''}`} />
                    </button>
                  </div>

                  <div className="space-y-2 mb-3">
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-gray-600">Sizes:</span>
                      <div className="flex flex-wrap gap-1">
                        {item.sizes.map((size, idx) => (
                          <span key={idx} className="px-2 py-1 bg-white border border-gray-200 rounded text-xs">
                            {size}
                          </span>
                        ))}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-gray-600">Colors:</span>
                      <div className="flex flex-wrap gap-1">
                        {item.colors.map((color, idx) => (
                          <span key={idx} className="px-2 py-1 bg-white border border-gray-200 rounded text-xs capitalize">
                            {color}
                          </span>
                        ))}
                      </div>
                    </div>
                    <div className="text-sm font-medium text-gray-800">{item.price}</div>
                  </div>

                  <button
                    onClick={() => onItemSelect && onItemSelect(item)}
                    className="w-full bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white py-2 px-4 rounded-lg font-medium transition-all duration-300 transform hover:scale-105 flex items-center justify-center space-x-2"
                  >
                    <ShoppingBag className="w-4 h-4" />
                    <span>View Details</span>
                  </button>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Favorites Section */}
      {favorites.length > 0 && (
        <div className="mt-8 p-4 bg-gradient-to-r from-pink-50 to-red-50 rounded-xl border border-pink-100">
          <h3 className="text-lg font-semibold text-gray-800 mb-3 flex items-center space-x-2">
            <Heart className="w-5 h-5 text-red-500 fill-current" />
            <span>Your Favorites ({favorites.length})</span>
          </h3>
          <div className="flex flex-wrap gap-2">
            {favorites.map((item, index) => (
              <span key={index} className="px-3 py-1 bg-white rounded-full text-sm border border-pink-200">
                {item.name}
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ClothingRecommendation;
