// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";

/* ---------- Calibration ---------- */
const A4_WIDTH_CM = 21.0; // reference width
const CHEST_MULTIPLIER = 1.95; // bumped: shoulder flat → chest circumference
const UPPER_WAIST_FACTOR = 0.90; // hip width ≈ a bit wider than upper waist

/* ---------- Overlay helpers ---------- */
const UPPER = [
  [0,1],[1,2],[2,3],[3,7],
  [0,11],[0,12],
  [11,13],[13,15],
  [12,14],[14,16],
  [11,12],
  [11,23],[12,24],
];
const LOWER = [
  [23,25],[25,27],[27,31],
  [24,26],[26,28],[28,32],
  [23,24],
];
const drawSeg = (ctx, lm, a, b, color, width, W, H) => {
  const A = lm[a], B = lm[b];
  if (!A || !B) return;
  if ((A.visibility ?? 0) < 0.4 || (B.visibility ?? 0) < 0.4) return;
  ctx.strokeStyle = color; ctx.lineWidth = width;
  ctx.beginPath(); ctx.moveTo(A.x*W, A.y*H); ctx.lineTo(B.x*W, B.y*H); ctx.stroke();
};
const drawBBox = (ctx, lm, W, H, color="rgba(0,255,0,0.9)") => {
  const pts = lm.filter(p => (p?.visibility ?? 0) > 0.4);
  if (!pts.length) return;
  const xs = pts.map(p=>p.x*W), ys = pts.map(p=>p.y*H);
  const minX = Math.max(0, Math.min(...xs)), maxX = Math.min(W, Math.max(...xs));
  const minY = Math.max(0, Math.min(...ys)), maxY = Math.min(H, Math.max(...ys));
  const pad = 8; ctx.strokeStyle = color; ctx.lineWidth = 3;
  ctx.strokeRect(minX-pad, minY-pad, (maxX-minX)+pad*2, (maxY-minY)+pad*2);
};

/* ---------- Size recommendation (simple baseline) ---------- */
const recommendTopSize = (chestCm) => {
  if (!chestCm) return null;
  const table = [
    { size: "XS", min: 78, max: 86 },
    { size: "S",  min: 86, max: 94 },
    { size: "M",  min: 94, max: 102 },
    { size: "L",  min: 102, max: 110 },
    { size: "XL", min: 110, max: 118 },
    { size: "XXL",min: 118, max: 126 },
  ];
  const hit = table.find(r => chestCm >= r.min && chestCm < r.max);
  return hit?.size ?? (chestCm < table[0].min ? "XS" : "XXL+");
};

// Optional style hint based on proportions (very light heuristic)
const styleHint = (chestCm, shoulderCm) => {
  if (!chestCm || !shoulderCm) return "";
  const ratio = chestCm / (shoulderCm || 1); // ~3–5 normal range
  if (ratio < 3.6) return "Try regular/relaxed fits.";
  if (ratio > 4.6) return "Slim or tailored fits may suit better.";
  return "Regular fit is likely comfortable.";
};

export default function Measure() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);

  const [phase, setPhase] = useState("calibrate"); // 'calibrate' | 'measure' | 'locked'
  const [status, setStatus] = useState("Hold an A4 sheet at chest level and match the bar width.");
  const [barPx, setBarPx] = useState(300);
  const [cmPerPx, setCmPerPx] = useState(null);

  const [locked, setLocked] = useState(false);
  const [meas, setMeas] = useState(null); // { chestCm, shoulderCm, upperWaistCm, quality, _shoulderPx }

  // stability settings
  const GUIDE_MARGIN = 0.08;
  const STABILITY_FRAMES = 12;
  const MOVEMENT_EPS = 0.015;
  const recentShoulderN = useRef([]);
  const chestBuf = useRef([]);
  const shoulderBuf = useRef([]);
  const waistBuf = useRef([]);

  // sanity bounds to avoid bad locks
  const SHOULDER_MIN = 34, SHOULDER_MAX = 60; // cm plausible adult range on camera
  const CHEST_MIN = 75, CHEST_MAX = 140;      // cm plausible range

  const getDistance = useCallback((a,b)=>Math.hypot(a.x-b.x,a.y-b.y),[]);

  const stopCamera = () => {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      v?.srcObject && v.srcObject.getTracks().forEach(t=>t.stop());
      if (v) v.srcObject = null;
    } catch {}
  };

  const startCamera = async (pose) => {
    const v = videoRef.current;
    const camera = new Camera(v, {
      onFrame: async () => { await pose.send({ image: v }); },
      width: 1280, height: 720,
    });
    camRef.current = camera;
    camera.start();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const pose = new Pose({
      // Load from local public assets & force non‑SIMD to avoid WASM crash
      locateFile: (file) => `/mediapipe/pose/${file.replace('simd_', '')}`,
    });
    poseRef.current = pose;

    pose.setOptions({
      modelComplexity: /Mobi|Android/i.test(navigator.userAgent) ? 0 : 1, // smoother on phones
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((res) => {
      if (locked) return;

      const W = res.image.width, H = res.image.height;
      if (canvas.width !== W) canvas.width = W;
      if (canvas.height !== H) canvas.height = H;

      ctx.save();
      ctx.clearRect(0,0,W,H);
      ctx.drawImage(res.image,0,0,W,H);

      // guide box
      const gx = W*GUIDE_MARGIN, gy = H*GUIDE_MARGIN;
      const gW = W*(1-GUIDE_MARGIN*2), gH = H*(1-GUIDE_MARGIN*2);
      ctx.strokeStyle = "rgba(125,255,160,0.85)";
      ctx.lineWidth = 3; ctx.strokeRect(gx, gy, gW, gH);

      // calibration bar
      if (phase === "calibrate") {
        const barH = 14;
        ctx.fillStyle = "rgba(255,255,255,0.95)";
        ctx.fillRect((W-barPx)/2, H*0.1, barPx, barH);
        ctx.font = "14px system-ui, sans-serif";
        ctx.fillStyle = "rgba(255,255,255,0.95)";
        ctx.fillText(
          "Match this bar to A4 width (21 cm) at chest depth.",
          Math.max(12,(W-360)/2), H*0.1-8
        );
      }

      const lm = res.poseLandmarks;
      if (!lm) { setStatus("Detecting body…"); ctx.restore(); return; }

      // overlay (screenshot style)
      drawBBox(ctx, lm, W, H);
      UPPER.forEach(([a,b])=>drawSeg(ctx,lm,a,b,"#2aa4ff",3,W,H));
      LOWER.forEach(([a,b])=>drawSeg(ctx,lm,a,b,"#ff9933",3,W,H));

      const vis = (i) => (lm[i]?.visibility ?? 0) > 0.45;
      const shouldersOK = vis(11) && vis(12);
      const hipsOK = vis(23) || vis(24);
      if (!(shouldersOK && hipsOK)) { setStatus("Show shoulders & upper torso."); ctx.restore(); return; }

      const Lsh = lm[11], Rsh = lm[12];
      const Lhip = lm[23], Rhip = lm[24];
      const hip = vis(23) ? Lhip : Rhip;

      // key dots
      const shMid = { x:(Lsh.x+Rsh.x)/2, y:(Lsh.y+Rsh.y)/2 };
      ctx.fillStyle = "rgba(0,200,255,1)";
      [Lsh, Rsh, shMid, hip].forEach(p => { ctx.beginPath(); ctx.arc(p.x*W, p.y*H, 6, 0, Math.PI*2); ctx.fill(); });

      // must be inside guide
      const inside = [Lsh,Rsh,hip].every(p => p.x*W>=gx && p.x*W<=gx+gW && p.y*H>=gy && p.y*H<=gy+gH);
      if (!inside) { setStatus("Step back/center inside the frame."); ctx.restore(); return; }

      if (phase === "calibrate") { setStatus("Adjust bar → Set calibration."); ctx.restore(); return; }
      if (!cmPerPx) { setStatus("Click Set calibration first."); ctx.restore(); return; }

      // normalized stability
      const shoulderWidthN = getDistance(Lsh,Rsh);
      const torsoHeightN = getDistance(shMid, hip);
      if (torsoHeightN <= 0) { setStatus("Hold still…"); ctx.restore(); return; }

      const bufN = recentShoulderN.current;
      bufN.push(shoulderWidthN); if (bufN.length > STABILITY_FRAMES) bufN.shift();
      const avg = bufN.reduce((a,b)=>a+b,0)/bufN.length;
      const maxDev = Math.max(...bufN.map(v=>Math.abs(v-avg)));

      // ----- measurements in cm -----
      const shoulderPx = Math.abs((Lsh.x - Rsh.x) * W);
      const shoulderCm = shoulderPx * cmPerPx;

      const chestCmInstant = shoulderCm * CHEST_MULTIPLIER;

      let upperWaistCmInstant = null;
      if (vis(23) && vis(24)) {
        const hipFlatCm = Math.abs((Lhip.x - Rhip.x) * W) * cmPerPx;
        upperWaistCmInstant = hipFlatCm * UPPER_WAIST_FACTOR;
      }

      // median smoothing
      const pushMed = (ref, val, n=15) => {
        if (val==null) return null;
        ref.current.push(val);
        if (ref.current.length>n) ref.current.shift();
        const s=[...ref.current].sort((a,b)=>a-b);
        const m=Math.floor(s.length/2);
        return s.length%2 ? s[m] : (s[m-1]+s[m])/2;
      };
      const chestMed = pushMed(chestBuf, chestCmInstant);
      const shoulderMed = pushMed(shoulderBuf, shoulderCm);
      const waistMed = pushMed(waistBuf, upperWaistCmInstant);

      // stability: normalized shoulders + cm spread
      const spread = chestBuf.current.length>=8 ? Math.max(...chestBuf.current)-Math.min(...chestBuf.current) : 999;
      const stable = bufN.length>=STABILITY_FRAMES && maxDev < MOVEMENT_EPS && spread < 1.8; // slightly relaxed

      // ----- SANITY GUARD: avoid bad locks when calibration is off -----
      const shoulderOk = shoulderMed > SHOULDER_MIN && shoulderMed < SHOULDER_MAX;
      const chestOk = chestMed > CHEST_MIN && chestMed < CHEST_MAX;
      if (stable && chestMed && shoulderMed) {
        if (!shoulderOk || !chestOk) {
          setStatus("Calibration off — hold A4 at chest depth, match bar, click Set calibration.");
          ctx.restore();
          return; // do not lock with bad numbers
        }
        const out = {
          chestCm: Number(chestMed.toFixed(1)),
          shoulderCm: Number(shoulderMed.toFixed(1)),
          upperWaistCm: waistMed ? Number(waistMed.toFixed(1)) : undefined,
          quality: { frames: chestBuf.current.length, spreadCm: Number(spread.toFixed(2)) }
        };
        // debug only
        out._shoulderPx = Number(shoulderPx.toFixed(1));

        setMeas(out);
        setLocked(true);
        setPhase("locked");
        setStatus("Locked ✔️");
        stopCamera();
      } else {
        setStatus("Hold still…");
      }

      ctx.restore();
    });

    startCamera(pose);
    return () => { stopCamera(); };
  }, [phase, cmPerPx, getDistance, locked, barPx]);

  const setCalibration = () => {
    if (barPx < 40) return;
    setCmPerPx(A4_WIDTH_CM / barPx);
    setPhase("measure");
    setStatus("Calibration set. Stand centered; shoulders & upper torso visible.");
    // reset buffers
    chestBuf.current = []; shoulderBuf.current = []; waistBuf.current = []; recentShoulderN.current = [];
  };

  const retake = () => {
    setLocked(false); setMeas(null); setPhase("measure");
    setStatus("Stand centered; hold still 1–2 seconds.");
    chestBuf.current = []; shoulderBuf.current = []; waistBuf.current = []; recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };

  const recalibrate = () => {
    setLocked(false); setMeas(null); setPhase("calibrate");
    setStatus("Hold an A4 sheet at chest level and match the bar width.");
    chestBuf.current = []; shoulderBuf.current = []; waistBuf.current = []; recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="relative w-full min-h-screen bg-gradient-to-b from-[#0b0f14] to-[#111827] flex flex-col items-center">
      {/* Header */}
      <div className="w-full max-w-6xl flex items-center justify-between px-4 pt-4">
        <div className="text-white font-semibold text-lg">AI Fashion Fit — Upper Body (Measurements)</div>
        <div className="bg-white/90 rounded-full px-3 py-1 text-sm shadow">{status}</div>
      </div>

      {/* DEBUG PANEL */}
      <div className="mt-3 text-xs text-white/70">
        cmPerPx: {cmPerPx?.toFixed?.(4) ?? "—"} | shoulderPx: {meas?._shoulderPx ?? "—"} | shoulderCm: {meas?.shoulderCm ?? "—"} | chestCm: {meas?.chestCm ?? "—"}
      </div>

      {/* Camera */}
      <div className="relative w-full max-w-6xl aspect-video mt-4 rounded-2xl overflow-hidden shadow-2xl ring-1 ring-white/10">
        <video ref={videoRef} className="absolute inset-0 w-full h-full object-contain bg-black" autoPlay muted playsInline />
        <canvas ref={canvasRef} className="absolute inset-0 w-full h-full object-contain pointer-events-none" />

        {/* Calibration UI */}
        {phase === "calibrate" && (
          <div className="absolute bottom-4 left-1/2 -translate-x-1/2 bg-white/95 backdrop-blur rounded-2xl px-4 py-3 flex items-center gap-4 shadow-lg">
            <div className="text-sm font-medium">A4 width match</div>
            <input type="range" min={120} max={900} value={barPx} onChange={(e)=>setBarPx(Number(e.target.value))} className="w-72 accent-purple-600" />
            <button onClick={setCalibration} className="px-4 py-2 rounded-xl bg-purple-600 text-white hover:bg-purple-700 text-sm shadow">
              Set calibration
            </button>
          </div>
        )}

        {/* Results */}
        {phase === "locked" && meas && (
          <div className="absolute inset-0 bg-black/60 flex items-center justify-center p-4">
            <div className="bg-white max-w-md w-full rounded-2xl p-6 shadow-2xl">
              <div className="text-xl font-bold mb-3">Upper‑Body Measurements</div>
              <div className="grid grid-cols-2 gap-y-1 text-[15px]">
                <div>Chest (circ.)</div><div className="font-semibold">{meas.chestCm} cm</div>
                <div>Shoulder width</div><div className="font-semibold">{meas.shoulderCm} cm</div>
                {meas.upperWaistCm !== undefined && (
                  <>
                    <div>Upper‑waist (flat)</div><div className="font-semibold">{meas.upperWaistCm} cm</div>
                  </>
                )}
              </div>

              {/* Suggested size + style */}
              <div className="mt-4 p-3 rounded-xl bg-purple-50 border border-purple-200">
                <div className="text-sm text-purple-900">Suggested top size</div>
                <div className="text-lg font-semibold text-purple-900">
                  {recommendTopSize(meas.chestCm)}
                </div>
                <div className="text-xs text-purple-700 mt-1">
                  {styleHint(meas.chestCm, meas.shoulderCm)} Based on chest circumference; refine per brand later.
                </div>
              </div>

              <div className="mt-3 text-xs text-gray-600">
                Quality — frames: {meas.quality.frames}, spread: {meas.quality.spreadCm} cm
              </div>
              <div className="mt-5 flex gap-2">
                <button onClick={retake} className="px-4 py-2 rounded-xl bg-black text-white hover:opacity-90">Retake</button>
                <button onClick={recalibrate} className="px-4 py-2 rounded-xl bg-gray-200 hover:bg-gray-300">Re‑calibrate</button>
                <button onClick={()=>window.print()} className="px-4 py-2 rounded-xl bg-purple-600 text-white hover:bg-purple-700">Save / Print</button>
              </div>
            </div>
          </div>
        )}
      </div>

      <p className="mt-3 text-white/60 text-sm text-center px-4">
        Tip: keep the A4 sheet flat at chest depth during calibration. Stand square to the camera. Hold still for 1–2 seconds.
      </p>
    </div>
  );
}
