// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { Camera as CameraIcon, Sparkles, Activity, Target, RotateCcw, Download } from 'lucide-react';

/* ---------- Calibration ---------- */
const A4_WIDTH_CM = 21.0; // reference width
const CHEST_MULTIPLIER = 1.75; // shoulder flat → chest circumference (tune 1.70–1.85)
const UPPER_WAIST_FACTOR = 0.90; // hip width ≈ a bit wider than upper waist

/* ---------- Overlay helpers ---------- */
const UPPER = [
  [0,1],[1,2],[2,3],[3,7],
  [0,11],[0,12],
  [11,13],[13,15],
  [12,14],[14,16],
  [11,12],
  [11,23],[12,24],
];
const LOWER = [
  [23,25],[25,27],[27,31],
  [24,26],[26,28],[28,32],
  [23,24],
];
const drawSeg = (ctx, lm, a, b, color, width, W, H) => {
  const A = lm[a], B = lm[b];
  if (!A || !B) return;
  if ((A.visibility ?? 0) < 0.4 || (B.visibility ?? 0) < 0.4) return;
  ctx.strokeStyle = color; ctx.lineWidth = width;
  ctx.beginPath(); ctx.moveTo(A.x*W, A.y*H); ctx.lineTo(B.x*W, B.y*H); ctx.stroke();
};
const drawBBox = (ctx, lm, W, H, color="rgba(0,255,0,0.9)") => {
  const pts = lm.filter(p => (p?.visibility ?? 0) > 0.4);
  if (!pts.length) return;
  const xs = pts.map(p=>p.x*W), ys = pts.map(p=>p.y*H);
  const minX = Math.max(0, Math.min(...xs)), maxX = Math.min(W, Math.max(...xs));
  const minY = Math.max(0, Math.min(...ys)), maxY = Math.min(H, Math.max(...ys));
  const pad = 8; ctx.strokeStyle = color; ctx.lineWidth = 3;
  ctx.strokeRect(minX-pad, minY-pad, (maxX-minX)+pad*2, (maxY-minY)+pad*2);
};

/* ---------- Size recommendation (using all three measurements) ---------- */
const recommendTopSize = (chestCm, waistCm, shoulderCm) => {
  if (!chestCm || !waistCm || !shoulderCm || chestCm <= 0 || waistCm <= 0 || shoulderCm <= 0) {
    return null;
  }

  // UPPER BODY MEASUREMENT REQUIREMENTS:
  // 1. Chest: Measured around the fullest part of the chest (bust for women, chest for men)
  // 2. Shoulder: Measured across the back from shoulder tip to shoulder tip
  // 3. Waist: Measured around the natural waistline (narrowest part of torso)

  // REALISTIC SIZE RANGES FOR ADULTS (in cm):
  // XXS: Chest 76-81, Shoulder 35-38, Waist 58-63
  // XS:  Chest 81-86, Shoulder 38-41, Waist 63-68  
  // S:   Chest 86-91, Shoulder 41-44, Waist 68-73
  // M:   Chest 91-96, Shoulder 44-47, Waist 73-78
  // L:   Chest 96-101, Shoulder 47-50, Waist 78-83
  // XL:  Chest 101-106, Shoulder 50-53, Waist 83-88
  // XXL: Chest 106-111, Shoulder 53-56, Waist 88-93
  // 3XL: Chest 111-116, Shoulder 56-59, Waist 93-98
  // 4XL: Chest 116+, Shoulder 59+, Waist 98+

  // BALANCED SIZE CALCULATION - All 3 measurements matter equally!

  // 1. Calculate individual size scores for each measurement
  const getSizeScore = (value, ranges) => {
    if (value < ranges.XXS.max) return 0;      // XXS
    if (value < ranges.XS.max) return 1;       // XS
    if (value < ranges.S.max) return 2;        // S
    if (value < ranges.M.max) return 3;        // M
    if (value < ranges.L.max) return 4;        // L
    if (value < ranges.XL.max) return 5;       // XL
    if (value < ranges.XXL.max) return 6;      // XXL
    if (value < ranges.threeXL.max) return 7;  // 3XL
    return 8;                                   // 4XL
  };

  // Size ranges for each measurement type
  const chestRanges = {
    XXS: { min: 0, max: 81 },
    XS: { min: 81, max: 86 },
    S: { min: 86, max: 91 },
    M: { min: 91, max: 96 },
    L: { min: 96, max: 101 },
    XL: { min: 101, max: 106 },
    XXL: { min: 106, max: 111 },
    threeXL: { min: 111, max: 116 },
    fourXL: { min: 116, max: 999 }
  };

  const shoulderRanges = {
    XXS: { min: 0, max: 38 },
    XS: { min: 38, max: 41 },
    S: { min: 41, max: 44 },
    M: { min: 44, max: 47 },
    L: { min: 47, max: 50 },
    XL: { min: 50, max: 53 },
    XXL: { min: 53, max: 56 },
    threeXL: { min: 56, max: 59 },
    fourXL: { min: 59, max: 999 }
  };

  const waistRanges = {
    XXS: { min: 0, max: 63 },
    XS: { min: 63, max: 68 },
    S: { min: 68, max: 73 },
    M: { min: 73, max: 78 },
    L: { min: 78, max: 83 },
    XL: { min: 83, max: 88 },
    XXL: { min: 88, max: 93 },
    threeXL: { min: 93, max: 98 },
    fourXL: { min: 98, max: 999 }
  };

  // 2. Get individual size scores
  const chestScore = getSizeScore(chestCm, chestRanges);
  const shoulderScore = getSizeScore(shoulderCm, shoulderRanges);
  const waistScore = getSizeScore(waistCm, waistRanges);

  // 3. Calculate weighted average (all measurements equally important)
  const averageScore = (chestScore + shoulderScore + waistScore) / 3;

  // 4. Apply body proportion adjustments
  let finalScore = averageScore;

  // Waist-to-chest ratio adjustment (body shape indicator)
  const waistChestRatio = waistCm / chestCm;
  if (waistChestRatio < 0.70) {
    // Very slim/athletic build - go down slightly
    finalScore -= 0.3;
  } else if (waistChestRatio > 0.90) {
    // Fuller build - go up slightly
    finalScore += 0.3;
  }

  // Shoulder-to-chest ratio adjustment (shoulder width indicator)
  const shoulderChestRatio = shoulderCm / chestCm;
  if (shoulderChestRatio > 0.60) {
    // Very broad shoulders - go up slightly
    finalScore += 0.2;
  } else if (shoulderChestRatio < 0.40) {
    // Very narrow shoulders - go down slightly
    finalScore -= 0.2;
  }

  // 5. Convert score to size with bounds checking
  const sizeOrder = ['XXS', 'XS', 'S', 'M', 'L', 'XL', 'XXL', '3XL', '4XL'];
  const sizeIndex = Math.max(0, Math.min(sizeOrder.length - 1, Math.round(finalScore)));
  const finalSize = sizeOrder[sizeIndex];

  // 6. Enhanced logging for debugging
  console.log(`🎯 BALANCED UPPER BODY SIZE CALCULATION:`, {
    measurements: {
      chestCm: chestCm.toFixed(1),
      shoulderCm: shoulderCm.toFixed(1),
      waistCm: waistCm.toFixed(1)
    },
    individualScores: {
      chestScore: `${chestScore} (${sizeOrder[chestScore]})`,
      shoulderScore: `${shoulderScore} (${sizeOrder[shoulderScore]})`,
      waistScore: `${waistScore} (${sizeOrder[waistScore]})`
    },
    calculation: {
      averageScore: averageScore.toFixed(2),
      proportionAdjustments: {
        waistChestRatio: waistChestRatio.toFixed(3),
        shoulderChestRatio: shoulderChestRatio.toFixed(3)
      },
      finalScore: finalScore.toFixed(2),
      finalSize
    },
    recommendations: {
      fit: getFitRecommendation(chestCm, shoulderCm, waistCm),
      style: getStyleRecommendation(chestCm, shoulderCm),
      dominantMeasurement: getDominantMeasurement(chestCm, shoulderCm, waistCm)
    }
  });

  return finalSize;
};

// Helper function to identify which measurement is most dominant
const getDominantMeasurement = (chestCm, shoulderCm, waistCm) => {
  const chestScore = Math.abs(chestCm - 96); // Distance from M size (96cm)
  const shoulderScore = Math.abs(shoulderCm - 45.5); // Distance from M size (45.5cm)
  const waistScore = Math.abs(waistCm - 75.5); // Distance from M size (75.5cm)

  const minScore = Math.min(chestScore, shoulderScore, waistScore);

  if (minScore === chestScore) return "Chest measurement is most dominant";
  if (minScore === shoulderScore) return "Shoulder measurement is most dominant";
  return "Waist measurement is most dominant";
};

// Helper function to validate measurements
const validateMeasurements = (chestCm, shoulderCm, waistCm) => {
  const warnings = [];
  const errors = [];

  // Check for unrealistic measurements
  if (chestCm < 60 || chestCm > 150) {
    errors.push(`Chest measurement (${chestCm}cm) seems unrealistic. Expected range: 60-150cm`);
  } else if (chestCm < 70 || chestCm > 130) {
    warnings.push(`Chest measurement (${chestCm}cm) is outside normal adult range (70-130cm)`);
  }

  if (shoulderCm < 25 || shoulderCm > 80) {
    errors.push(`Shoulder measurement (${shoulderCm}cm) seems unrealistic. Expected range: 25-80cm`);
  } else if (shoulderCm < 35 || shoulderCm > 65) {
    warnings.push(`Shoulder measurement (${shoulderCm}cm) is outside normal adult range (35-65cm)`);
  }

  if (waistCm < 40 || waistCm > 120) {
    errors.push(`Waist measurement (${waistCm}cm) seems unrealistic. Expected range: 40-120cm`);
  } else if (waistCm < 55 || waistCm > 100) {
    warnings.push(`Waist measurement (${waistCm}cm) is outside normal adult range (55-100cm)`);
  }

  // Check for proportion issues
  if (chestCm && shoulderCm && waistCm) {
    const shoulderChestRatio = shoulderCm / chestCm;
    const waistChestRatio = waistCm / chestCm;

    if (shoulderChestRatio < 0.3 || shoulderChestRatio > 0.8) {
      warnings.push(`Shoulder-to-chest ratio (${shoulderChestRatio.toFixed(2)}) seems unusual. Expected: 0.4-0.7`);
    }

    if (waistChestRatio < 0.5 || waistChestRatio > 1.1) {
      warnings.push(`Waist-to-chest ratio (${waistChestRatio.toFixed(2)}) seems unusual. Expected: 0.6-1.0`);
    }
  }

  return { warnings, errors, isValid: errors.length === 0 };
};

// Helper function to recommend fit type
const getFitRecommendation = (chestCm, shoulderCm, waistCm) => {
  if (!chestCm || !shoulderCm || !waistCm) return "Standard fit recommended";

  const shoulderChestRatio = shoulderCm / chestCm;
  const waistChestRatio = waistCm / chestCm;

  if (shoulderChestRatio > 0.55 && waistChestRatio < 0.80) {
    return "Athletic fit recommended (broad shoulders, slim waist)";
  } else if (shoulderChestRatio < 0.45 && waistChestRatio > 0.85) {
    return "Relaxed fit recommended (narrow shoulders, fuller waist)";
  } else if (waistChestRatio < 0.75) {
    return "Slim fit recommended (athletic build)";
  } else if (waistChestRatio > 0.90) {
    return "Regular/comfortable fit recommended";
  }

  return "Standard fit recommended";
};

// Helper function to recommend style
const getStyleRecommendation = (chestCm, shoulderCm) => {
  if (!chestCm || !shoulderCm) return "Regular style recommended";

  const ratio = chestCm / shoulderCm;
  if (ratio < 2.0) {
    return "Consider relaxed/oversized styles";
  } else if (ratio > 2.5) {
    return "Consider fitted/tailored styles";
  }

  return "Regular style recommended";
};

// Optional style hint based on proportions (very light heuristic)
const styleHint = (chestCm, shoulderCm) => {
  if (!chestCm || !shoulderCm) return "";
  const ratio = chestCm / (shoulderCm || 1); // ~3–5 normal range
  if (ratio < 3.6) return "Try regular/relaxed fits.";
  if (ratio > 4.6) return "Slim or tailored fits may suit better.";
  return "Regular fit is likely comfortable.";
};

// Add error handling wrapper at the top of the component
const MediaPipeErrorBoundary = ({ children }) => {
  const [hasError, setHasError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  if (hasError) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-indigo-900 flex items-center justify-center">
        <div className="bg-white/95 backdrop-blur-md rounded-3xl p-8 max-w-lg text-center">
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">⚠️</span>
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-4">MediaPipe Error</h2>
          <p className="text-gray-600 mb-6">{errorMessage}</p>
          <div className="space-y-3">
            <button 
              onClick={() => window.location.reload()}
              className="w-full bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-xl font-medium"
            >
              🔄 Refresh Page
            </button>
            <button 
              onClick={() => setHasError(false)}
              className="w-full bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-3 rounded-xl font-medium"
            >
              🚫 Dismiss
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <ErrorBoundary
      onError={(error) => {
        console.error('MediaPipe Error:', error);
        setErrorMessage(error.message || 'An error occurred with MediaPipe');
        setHasError(true);
      }}
    >
      {children}
    </ErrorBoundary>
  );
};

// Simple ErrorBoundary component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    if (this.props.onError) {
      this.props.onError(error);
    }
  }

  render() {
    if (this.state.hasError) {
      return null; // Let parent handle the error display
    }
    return this.props.children;
  }
}

