        { point: shMid, color: "#10B981" },
        { point: hip, color: "#F59E0B" }
      ];

      keyPoints.forEach(({point, color}) => {
        if (point) {
          // Glow effect
          ctx.shadowColor = color;
          ctx.shadowBlur = 15;
          ctx.fillStyle = color;
          ctx.beginPath(); 
          ctx.arc(point.x*W, point.y*H, 8, 0, Math.PI*2); 
          ctx.fill();

          // Inner bright dot
          ctx.shadowBlur = 0;
          ctx.fillStyle = "white";
          ctx.beginPath(); 
          ctx.arc(point.x*W, point.y*H, 4, 0, Math.PI*2); 
          ctx.fill();
