                      <div className="text-sm text-purple-500">Waist (cm)</div>
                      <div className="text-xs text-purple-400 mt-1">
                        {meas.upperWaistCm ? (meas.upperWaistCm < 68 ? 'Slim' : 
                                             meas.upperWaistCm < 78 ? 'Standard' : 
                                             meas.upperWaistCm < 88 ? 'Full' : 'Very Full') : 'N/A'}
                      </div>
                    </div>
                  </div>

                  {/* Size Recommendation with Explanation */}
                  <div className="bg-gradient-to-r from-blue-50 to-emerald-50 rounded-xl p-4 mb-6 border border-blue-200">
                    <div className="text-lg font-semibold text-gray-700 mb-2">🎯 Recommended Size</div>
                    <div className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-emerald-600 bg-clip-text text-transparent mb-2">
                      {recommendTopSize(meas.chestCm, meas.upperWaistCm, meas.shoulderCm)}
                    </div>
                    <div className="text-sm text-gray-600">
                      Based on chest measurement: {meas.chestCm?.toFixed(1)} cm
                    </div>
                  </div>

                  {/* Fit and Style Recommendations */}
                  {meas.chestCm && meas.shoulderCm && meas.upperWaistCm && (
                    <div className="bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl p-4 mb-6 border border-purple-200">
                      <h4 className="font-semibold text-purple-800 mb-2">💡 Fit & Style Recommendations</h4>
                      <div className="text-sm text-purple-700 space-y-2">
                        <p><strong>Fit:</strong> {getFitRecommendation(meas.chestCm, meas.shoulderCm, meas.upperWaistCm)}</p>
                        <p><strong>Style:</strong> {getStyleRecommendation(meas.chestCm, meas.shoulderCm)}</p>
                      </div>
                    </div>
                  )}

                  {/* Measurement Validation */}
                  {meas.chestCm && meas.shoulderCm && meas.upperWaistCm && (() => {
                    const validation = validateMeasurements(meas.chestCm, meas.shoulderCm, meas.upperWaistCm);
                    if (validation.errors.length > 0 || validation.warnings.length > 0) {
                      return (
                        <div className="bg-yellow-50 rounded-xl p-4 mb-6 border border-yellow-200">
                          <h4 className="font-semibold text-yellow-800 mb-2">⚠️ Measurement Validation</h4>
                          {validation.errors.length > 0 && (
                            <div className="mb-3">
                              <p className="text-sm font-medium text-red-700 mb-1">❌ Critical Issues:</p>
                              <ul className="text-xs text-red-600 space-y-1">
                                {validation.errors.map((error, index) => (
                                  <li key={index}>• {error}</li>
                                ))}
                              </ul>
                            </div>
                          )}
                          {validation.warnings.length > 0 && (
                            <div>
                              <p className="text-sm font-medium text-yellow-700 mb-1">⚠️ Warnings:</p>
                              <ul className="text-xs text-yellow-600 space-y-1">
                                {validation.warnings.map((warning, index) => (
                                  <li key={index}>• {warning}</li>
                                ))}
                              </ul>
                            </div>
                          )}
                          <div className="mt-3 p-2 bg-blue-50 rounded border border-blue-200">
                            <p className="text-xs text-blue-700">
                              <strong>💡 Tip:</strong> If measurements seem incorrect, try recalibrating with the A4 sheet or retaking the measurement.
                            </p>
                          </div>
                        </div>
                      );
                    }
                    return null;
                  })()}

                  {/* Size Chart Reference */}
                  <div className="bg-gray-50 rounded-xl p-4 mb-6 border border-gray-200">
                    <h4 className="font-semibold text-gray-800 mb-2">📋 Size Chart Reference</h4>
                    <div className="text-xs text-gray-600 grid grid-cols-4 gap-2">
                      <div><strong>XXS:</strong> 76-81cm</div>
                      <div><strong>XS:</strong> 81-86cm</div>
                      <div><strong>S:</strong> 86-91cm</div>
                      <div><strong>M:</strong> 91-96cm</div>
                      <div><strong>L:</strong> 96-101cm</div>
                      <div><strong>XL:</strong> 101-106cm</div>
                      <div><strong>XXL:</strong> 106-111cm</div>
                      <div><strong>3XL:</strong> 111-116cm</div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex flex-col sm:flex-row gap-3 justify-center">
                    <button 
                      onClick={retake}
                      className="bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-3 rounded-xl font-medium transition-all duration-300 border-2 border-gray-300"
                    >
                      🔄 Retake
                    </button>
                    <button 
                      onClick={recalibrate}
                      className="bg-blue-100 hover:bg-blue-200 text-blue-700 px-6 py-3 rounded-xl font-medium transition-all duration-300 border-2 border-blue-300"
                    >
                      🎯 Recalibrate
                    </button>
                    <button 
                      onClick={() => {
                        const recommendedSize = recommendTopSize(meas.chestCm, meas.upperWaistCm, meas.shoulderCm);
                        if (recommendedSize) {
                          // Pass all measurement data to recommendations page
                          const measurementData = encodeURIComponent(JSON.stringify({
                            size: recommendedSize,
                            gender: 'Men',
                            measurements: {
                              chestCm: meas.chestCm,
                              shoulderCm: meas.shoulderCm,
                              upperWaistCm: meas.upperWaistCm
                            }
                          }));
                          window.location.href = `/recommendations?data=${measurementData}`;
                        }
                      }}
                      className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-6 py-3 rounded-xl font-medium transition-all duration-300 shadow-lg"
                    >
                      ✨ Get Recommendations
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </MediaPipeErrorBoundary>
  );
}
