// src/pages/Measure.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";
import PoseOverlay from "../components/PoseOverlay";

const poseSteps = ["front", "side", "back"]; // still here if you want multi-view shots

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [currentStep, setCurrentStep] = useState(0);
  const [userHeight, setUserHeight] = useState(170); // cm
  const [torsoPercent, setTorsoPercent] = useState(28); // % of total height ‚Üí tunable
  const [landmarksDetected, setLandmarksDetected] = useState(false);

  const [measurements, setMeasurements] = useState({ chest: null });
  const [suggestedSize, setSuggestedSize] = useState("");
  const [capturedImages, setCapturedImages] = useState({});

  // --- helpers ---
  const getDistance = useCallback((a, b) => {
    const dx = a.x - b.x;
    const dy = a.y - b.y;
    return Math.hypot(dx, dy);
  }, []);

  const midPoint = (a, b) => ({ x: (a.x + b.x) / 2, y: (a.y + b.y) / 2 });

  // Very simple T-shirt size suggestion using chest (cm)
  const getSizeSuggestion = (chest) => {
    // Adjust bands to your chart
    if (chest >= 106) return "XL";
    if (chest >= 100) return "L";
    if (chest >= 94) return "M";
    if (chest >= 88) return "S";
    return "Custom";
  };

  const handleCapture = () => {
    const canvas = canvasRef.current;
    const dataUrl = canvas.toDataURL("image/png");
    const step = poseSteps[currentStep];
    setCapturedImages((prev) => ({ ...prev, [step]: dataUrl }));
    if (currentStep < poseSteps.length - 1) setCurrentStep((s) => s + 1);
    else alert("‚úÖ All poses captured!");
  };

  useEffect(() => {
    const videoEl = videoRef.current;
    const canvasEl = canvasRef.current;
    const ctx = canvasEl.getContext("2d");

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      const lm = results.poseLandmarks;

      // match canvas size to frame
      const W = results.image.width;
      const H = results.image.height;
      if (canvasEl.width !== W) canvasEl.width = W;
      if (canvasEl.height !== H) canvasEl.height = H;

      ctx.save();
      ctx.clearRect(0, 0, W, H);
      ctx.drawImage(results.image, 0, 0, W, H);

      if (lm) {
        // draw pose for reference
        drawConnectors(ctx, lm, Pose.POSE_CONNECTIONS, { lineWidth: 2 });
        drawLandmarks(ctx, lm, { lineWidth: 1 });

        // visibility helpers
        const vis = (id) => (lm[id]?.visibility ?? 0) > 0.5;

        // require shoulders + at least one hip (upper-body only)
        const shouldersOK = vis(11) && vis(12);
        const hipsOK = vis(23) || vis(24);

        if (shouldersOK && hipsOK) {
          setLandmarksDetected(true);

          // key points
          const Lsh = lm[11], Rsh = lm[12];
          const Lhip = lm[23], Rhip = lm[24];

          // pixel distances (normalized by frame later)
          const shoulderWidthPxN = getDistance(Lsh, Rsh); // normalized [0..1]
          const shMid = midPoint(Lsh, Rsh);

          // pick whichever hip is visible for torso height
          const hip = vis(23) ? Lhip : Rhip;
          const torsoPxN = getDistance(shMid, hip); // normalized torso height (shoulder mid ‚Üí hip)

          if (torsoPxN > 0) {
            // Convert normalized distances to pixels
            const shoulderWidthPx = shoulderWidthPxN * W;
            const torsoPx = torsoPxN * H;

            // Scale: assume torso is X% of total height
            const torsoCm = (userHeight * torsoPercent) / 100.0; // real-world torso height in cm
            const pxToCmRatio = torsoCm / torsoPx; // cm per pixel

            // Chest circumference approximation from shoulder width (flat ‚Üí circumference)
            // Multiplier is empirical; tweak after testing with your size chart.
            const chestFlatCm = shoulderWidthPx * pxToCmRatio;
            const chestCircumferenceCm = chestFlatCm * 1.75; // multiplier ~1.6‚Äì1.9 typical

            const chest = Number(chestCircumferenceCm.toFixed(1));
            setMeasurements({ chest });
            setSuggestedSize(getSizeSuggestion(chest));

            // Upper‚Äëbody guide overlay
            ctx.fillStyle = "rgba(0, 255, 255, 0.25)";
            const boxW = W * 0.6;
            const boxH = H * 0.55;
            ctx.fillRect((W - boxW) / 2, (H - boxH) / 2 - H * 0.05, boxW, boxH);
          } else {
            setLandmarksDetected(false);
            setMeasurements({ chest: null });
            setSuggestedSize("");
          }
        } else {
          setLandmarksDetected(false);
          setMeasurements({ chest: null });
          setSuggestedSize("");
        }
      }

      ctx.restore();
    });

    // higher resolution helps shoulder/hip stability
    const camera = new Camera(videoEl, {
      onFrame: async () => {
        await pose.send({ image: videoEl });
      },
      width: 1280,
      height: 720,
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, [getDistance, userHeight, torsoPercent, currentStep]);

  return (
    <div className="relative w-full min-h-screen bg-black flex flex-col items-center">
      {/* Controls */}
      <div className="w-full max-w-5xl flex flex-wrap items-center gap-3 justify-between px-3 pt-2">
        <div className="flex items-center gap-2">
          <label className="text-white/90 text-sm">Height (cm)</label>
          <input
            type="number"
            value={userHeight}
            onChange={(e) => setUserHeight(Number(e.target.value))}
            className="p-2 bg-white rounded shadow-md text-sm w-28"
          />
        </div>

        <div className="flex items-center gap-2">
          <label className="text-white/90 text-sm">Torso % of height</label>
          <input
            type="number"
            min={22}
            max={34}
            value={torsoPercent}
            onChange={(e) => setTorsoPercent(Number(e.target.value))}
            className="p-2 bg-white rounded shadow-md text-sm w-20"
            title="Typical adult torso is ~26‚Äì30% of total height"
          />
        </div>

        <div className="bg-white/90 px-3 py-1 rounded text-sm font-medium">
          T‚ÄëShirt Mode ‚Ä¢ Step {currentStep + 1} of {poseSteps.length}: {poseSteps[currentStep].toUpperCase()}
        </div>
      </div>

      {/* Camera frame (no cropping) */}
      <div className="relative w-full max-w-5xl aspect-video mt-2">
        <video
          ref={videoRef}
          className="absolute inset-0 w-full h-full object-contain bg-black"
          autoPlay
          muted
          playsInline
        />
        <canvas ref={canvasRef} className="absolute inset-0 w-full h-full object-contain" />

        {/* Optional overlay component if you still want it */}
        <PoseOverlay view={poseSteps[currentStep]} />

        <div className="absolute bottom-4 left-4 text-white font-semibold text-sm sm:text-base">
          {landmarksDetected
            ? "‚úÖ Upper body detected (shoulders + hip). Hold still‚Ä¶"
            : "üîç Show shoulders and upper torso. Step back slightly if needed."}
        </div>

        <button
          onClick={handleCapture}
          className="absolute bottom-4 right-4 bg-purple-600 text-white px-4 py-2 rounded shadow-md hover:bg-purple-700"
        >
          Capture {poseSteps[currentStep].toUpperCase()}
        </button>

        {measurements.chest && (
          <div className="absolute top-3 left-3 bg-white/90 text-black p-4 rounded-lg shadow-md text-sm w-60 z-30">
            <div className="text-lg font-bold mb-1">üëï T‚ÄëShirt Sizing</div>
            <div>Chest (circ.): <b>{measurements.chest} cm</b></div>
            <div className="mt-1 text-purple-700 font-semibold">Suggested Size: {suggestedSize}</div>
            <div className="mt-1 text-xs text-gray-600">
              Tip: adjust ‚ÄúTorso %‚Äù if your angle is high/low.
            </div>
          </div>
        )}

        {Object.keys(capturedImages).length > 0 && (
          <div className="absolute bottom-20 left-1/2 -translate-x-1/2 flex gap-2 z-20">
            {Object.entries(capturedImages).map(([key, url]) => (
              <img
                key={key}
                src={url}
                alt={`${key} view`}
                className="w-16 h-24 object-cover border-2 border-white rounded"
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Measure;
