import React, { useRef, useEffect, useState, useCallback } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";

/* ---------- Sizing config ---------- */
const A4_WIDTH_CM = 21.0;          // calibration reference
const MULTIPLIER_CHEST = 1.75;     // shoulder flat â†’ chest circumference (tune 1.6â€“1.9)

const MEN_SIZE_CHART = [
  { size: "S",   min: 88,  max: 94  },
  { size: "M",   min: 95,  max: 102 },
  { size: "L",   min: 103, max: 110 },
  { size: "XL",  min: 111, max: 118 },
  { size: "2XL", min: 119, max: 126 },
  { size: "3XL", min: 127, max: 134 },
  { size: "4XL", min: 135, max: 145 },
];
const menSizeFromChest = (cm) => {
  for (const r of MEN_SIZE_CHART) if (cm >= r.min && cm <= r.max) return r.size;
  return cm < MEN_SIZE_CHART[0].min ? "XS/S" : "4XL+";
};

/* ---------- Overlay (screenshot style) ---------- */
const UPPER = [
  [0,1],[1,2],[2,3],[3,7],
  [0,11],[0,12],
  [11,13],[13,15],
  [12,14],[14,16],
  [11,12],
  [11,23],[12,24],
];
const LOWER = [
  [23,25],[25,27],[27,31],
  [24,26],[26,28],[28,32],
  [23,24],
];
const drawSeg = (ctx, lm, a, b, color, width, W, H) => {
  const A = lm[a], B = lm[b];
  if (!A || !B) return;
  if ((A.visibility ?? 0) < 0.4 || (B.visibility ?? 0) < 0.4) return;
  ctx.strokeStyle = color; ctx.lineWidth = width;
  ctx.beginPath(); ctx.moveTo(A.x*W, A.y*H); ctx.lineTo(B.x*W, B.y*H); ctx.stroke();
};
const drawBBox = (ctx, lm, W, H, color="rgba(0,255,0,0.9)") => {
  const pts = lm.filter(p => (p?.visibility ?? 0) > 0.4); if (!pts.length) return;
  const xs = pts.map(p=>p.x*W), ys = pts.map(p=>p.y*H);
  const minX = Math.max(0, Math.min(...xs)), maxX = Math.min(W, Math.max(...xs));
  const minY = Math.max(0, Math.min(...ys)), maxY = Math.min(H, Math.max(...ys));
  const pad = 8; ctx.strokeStyle = color; ctx.lineWidth = 3;
  ctx.strokeRect(minX-pad, minY-pad, (maxX-minX)+pad*2, (maxY-minY)+pad*2);
};

/* ---------- Component ---------- */
const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const camRef = useRef(null);

  const [phase, setPhase] = useState("calibrate"); // 'calibrate' | 'measure' | 'locked'
  const [status, setStatus] = useState("Hold an A4 sheet at chest level and match the bar width.");
  const [cmPerPx, setCmPerPx] = useState(null);
  const [barPx, setBarPx] = useState(300);

  const [locked, setLocked] = useState(false);
  const [result, setResult] = useState(null);

  // stability & UI
  const STABILITY_FRAMES = 12;
  const MOVEMENT_EPS = 0.015;
  const GUIDE_MARGIN = 0.08;
  const recentShoulderN = useRef([]);
  const chestBufRef = useRef([]);

  const getDistance = useCallback((a,b)=>Math.hypot(a.x-b.x,a.y-b.y),[]);

  const stopCamera = () => {
    try {
      camRef.current?.stop?.();
      const v = videoRef.current;
      v?.srcObject && v.srcObject.getTracks().forEach(t=>t.stop());
      if (v) v.srcObject = null;
    } catch {}
  };

  const startCamera = async (pose) => {
    const v = videoRef.current;
    const camera = new Camera(v, {
      onFrame: async () => { await pose.send({ image: v }); },
      width: 1280, height: 720,
    });
    camRef.current = camera;
    camera.start();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const pose = new Pose({
      // â­ Serve local files + force nonâ€‘SIMD to avoid CDN WASM crash
      locateFile: (file) => `/mediapipe/pose/${file.replace('simd_', '')}`,
    });
    poseRef.current = pose;

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((res) => {
      if (locked) return;

      const W = res.image.width, H = res.image.height;
      if (canvas.width !== W) canvas.width = W;
      if (canvas.height !== H) canvas.height = H;

      ctx.save();
      ctx.clearRect(0,0,W,H);
      ctx.drawImage(res.image,0,0,W,H);

      // soft vignette for nicer look
      const grd = ctx.createLinearGradient(0,0,0,H);
      grd.addColorStop(0,"rgba(0,0,0,0.10)"); grd.addColorStop(1,"rgba(0,0,0,0.15)");
      ctx.fillStyle = grd; ctx.fillRect(0,0,W,H);

      // guide box
      const gx = W*GUIDE_MARGIN, gy = H*GUIDE_MARGIN;
      const gW = W*(1-GUIDE_MARGIN*2), gH = H*(1-GUIDE_MARGIN*2);
      ctx.strokeStyle = "rgba(125, 255, 160, 0.85)";
      ctx.lineWidth = 3; ctx.strokeRect(gx, gy, gW, gH);

      // calibration bar (phase: calibrate)
      if (phase === "calibrate") {
        const barH = 14;
        ctx.fillStyle = "rgba(255,255,255,0.95)";
        ctx.fillRect((W-barPx)/2, H*0.1, barPx, barH);
        ctx.font = "14px system-ui, sans-serif";
        ctx.fillStyle = "rgba(255,255,255,0.95)";
        ctx.fillText("Match this bar to A4 width (21 cm) at chest depth.",
          Math.max(12,(W-360)/2), H*0.1-8);
      }

      const lm = res.poseLandmarks;
      if (!lm) { setStatus("Detecting bodyâ€¦"); ctx.restore(); return; }

      // overlay
      drawBBox(ctx, lm, W, H);
      UPPER.forEach(([a,b])=>drawSeg(ctx,lm,a,b,"#2aa4ff",3,W,H));
      LOWER.forEach(([a,b])=>drawSeg(ctx,lm,a,b,"#ff9933",3,W,H));

      const vis = (i) => (lm[i]?.visibility ?? 0) > 0.4;
      const shouldersOK = vis(11) && vis(12);
      const hipsOK = vis(23) || vis(24);
      if (!(shouldersOK && hipsOK)) { setStatus("Show shoulders & upper torso."); ctx.restore(); return; }

      const Lsh = lm[11], Rsh = lm[12];
      const hip = vis(23) ? lm[23] : lm[24];

      // draw key points
      const shMid = { x:(Lsh.x+Rsh.x)/2, y:(Lsh.y+Rsh.y)/2 };
      ctx.fillStyle = "rgba(0,200,255,1)";
      [Lsh, Rsh, shMid, hip].forEach(p => { ctx.beginPath(); ctx.arc(p.x*W, p.y*H, 6, 0, Math.PI*2); ctx.fill(); });

      // must be inside guide
      const inGuide = [Lsh,Rsh,hip].every(p => p.x*W>=gx && p.x*W<=gx+gW && p.y*H>=gy && p.y*H<=gy+gH);
      if (!inGuide) { setStatus("Step back/center yourself inside the frame."); ctx.restore(); return; }

      if (phase === "calibrate") {
        setStatus("Adjust bar to A4 width, then click Set calibration.");
        ctx.restore(); return;
      }

      // ------- MEASURE phase -------
      if (!cmPerPx) { setStatus("Click Set calibration first."); ctx.restore(); return; }

      // normalized for stability
      const shoulderWidthN = getDistance(Lsh,Rsh);
      const torsoHeightN = getDistance(shMid,hip);
      if (torsoHeightN <= 0) { setStatus("Hold stillâ€¦"); ctx.restore(); return; }

      // stability buffer on normalized shoulder width
      const bufN = recentShoulderN.current;
      bufN.push(shoulderWidthN); if (bufN.length > STABILITY_FRAMES) bufN.shift();
      const avg = bufN.reduce((a,b)=>a+b,0)/bufN.length;
      const maxDev = Math.max(...bufN.map(v=>Math.abs(v-avg)));

      // convert to pixels (use horizontal shoulder distance for stability)
      const Lx = Lsh.x*W, Rx = Rsh.x*W;
      const shoulderPx = Math.abs(Lx - Rx);               // ðŸ‘ˆ horizontal only (stable)
      const chestFlatCm = shoulderPx * cmPerPx;
      const chestCmInstant = chestFlatCm * MULTIPLIER_CHEST;

      // median smoothing on chest cm
      const b = chestBufRef.current; b.push(chestCmInstant); if (b.length > 15) b.shift();
      const median = (arr)=>{ const s=[...arr].sort((a,b)=>a-b); const m=Math.floor(s.length/2); return s.length%2?s[m]:(s[m-1]+s[m])/2; };
      const chestCmMed = median(b);
      const spread = b.length >= 8 ? Math.max(...b) - Math.min(...b) : 999;
      const stableCm = b.length >= 8 && spread < 1.2;     // <1.2 cm variation

      if (bufN.length >= STABILITY_FRAMES && maxDev < MOVEMENT_EPS && stableCm) {
        const chestCm = Number(chestCmMed.toFixed(1));
        const tshirt = menSizeFromChest(chestCm);
        const fit = chestCm <= MEN_SIZE_CHART.find(r=>r.size===tshirt)?.max ? "Slim/Regular" : "Regular/Relaxed";

        setResult({
          chestCm, tshirt, hoodie: tshirt==="S"?"S":tshirt, fit,
          debug: { cmPerPx: Number(cmPerPx.toFixed(4)), shoulderPx: Math.round(shoulderPx) }
        });
        setLocked(true); setPhase("locked"); setStatus("Locked âœ”ï¸"); stopCamera();
      } else {
        setStatus("Hold stillâ€¦");
      }

      ctx.restore();
    });

    startCamera(pose);
    return () => { stopCamera(); };
  }, [phase, cmPerPx, getDistance, locked, barPx]);

  const handleSetCalibration = () => {
    if (barPx < 40) return;
    setCmPerPx(A4_WIDTH_CM / barPx);  // cm per pixel
    setPhase("measure");
    setStatus("Calibration set. Stand centered; shoulders & upper torso visible.");
  };
  const handleRetake = () => {
    setLocked(false); setResult(null); setPhase("measure");
    setStatus("Stand centered; hold still 1â€“2 seconds.");
    chestBufRef.current = []; recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };
  const handleRecalibrate = () => {
    setLocked(false); setResult(null); setPhase("calibrate");
    setStatus("Hold an A4 sheet at chest level and match the bar width.");
    chestBufRef.current = []; recentShoulderN.current = [];
    if (poseRef.current) startCamera(poseRef.current);
  };

  return (
    <div className="relative w-full min-h-screen bg-gradient-to-b from-[#0b0f14] to-[#111827] flex flex-col items-center">
      {/* Header */}
      <div className="w-full max-w-6xl flex items-center justify-between px-4 pt-4">
        <div className="text-white font-semibold text-lg">AI Fashion Fit â€” Upper Body</div>
        <div className="bg-white/90 rounded-full px-3 py-1 text-sm shadow">{status}</div>
      </div>

      {/* Camera */}
      <div className="relative w-full max-w-6xl aspect-video mt-4 rounded-2xl overflow-hidden shadow-2xl ring-1 ring-white/10">
        <video ref={videoRef} className="absolute inset-0 w-full h-full object-contain bg-black" autoPlay muted playsInline />
        <canvas ref={canvasRef} className="absolute inset-0 w-full h-full object-contain pointer-events-none" />

        {/* Calibration UI */}
        {phase === "calibrate" && (
          <div className="absolute bottom-4 left-1/2 -translate-x-1/2 bg-white/95 backdrop-blur rounded-2xl px-4 py-3 flex items-center gap-4 shadow-lg">
            <div className="text-sm font-medium">A4 width match</div>
            <input type="range" min={120} max={900} value={barPx} onChange={(e)=>setBarPx(Number(e.target.value))} className="w-72 accent-purple-600" />
            <button onClick={handleSetCalibration} className="px-4 py-2 rounded-xl bg-purple-600 text-white hover:bg-purple-700 text-sm shadow">
              Set calibration
            </button>
          </div>
        )}

        {/* Result modal */}
        {phase === "locked" && result && (
          <div className="absolute inset-0 bg-black/60 flex items-center justify-center p-4">
            <div className="bg-white max-w-md w-full rounded-2xl p-6 shadow-2xl">
              <div className="text-xl font-bold mb-3">Recommended Sizes</div>
              <div className="grid grid-cols-2 gap-y-1 text-[15px]">
                <div>Chest</div><div className="font-semibold">{result.chestCm} cm</div>
                <div>ðŸ‘• Tâ€‘Shirt</div><div className="font-semibold">{result.tshirt}</div>
                <div>ðŸ§¥ Hoodie</div><div className="font-semibold">{result.hoodie}</div>
                <div>ðŸŽ¯ Fit</div><div className="font-semibold">{result.fit}</div>
              </div>
              <div className="mt-3 text-xs text-gray-600">
                Debug â€” cm/px: {result.debug.cmPerPx}, shoulderPx: {result.debug.shoulderPx}
              </div>
              <div className="mt-5 flex gap-2">
                <button onClick={handleRetake} className="px-4 py-2 rounded-xl bg-black text-white hover:opacity-90">Retake</button>
                <button onClick={handleRecalibrate} className="px-4 py-2 rounded-xl bg-gray-200 hover:bg-gray-300">Reâ€‘calibrate</button>
                <button onClick={()=>window.print()} className="px-4 py-2 rounded-xl bg-purple-600 text-white hover:bg-purple-700">Save / Print</button>
              </div>
            </div>
          </div>
        )}
      </div>

      <p className="mt-3 text-white/60 text-sm text-center px-4">
        Tip: hold the A4 sheet flat at chest depth during calibration. Stand square to the camera. Keep still for 1â€“2 seconds.
      </p>
    </div>
  );
};

export default Measure;
