import React from 'react';

const PoseOverlay = ({ view }) => {
  let positionStyles = '';

  if (view === 'front' || view === 'back') {
    positionStyles = 'top-[15%] left-[25%] w-[50%] h-[70%]';
  } else if (view === 'side') {
    positionStyles = 'top-[15%] left-[30%] w-[40%] h-[70%]';
  }

  const instructionText =
    view === 'front'
      ? 'Step 1: Stand facing the camera'
      : view === 'side'
      ? 'Step 2: Turn sideways (left or right)'
      : 'Step 3: Turn around, back to camera';

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      <div
        className={`absolute ${positionStyles} border-2 border-dashed border-purple-400 rounded-lg bg-black/30 flex flex-col items-center justify-center`}
      >
        <img
          src={`/images/pose-${view}.png`} // should be 'pose-front.png', 'pose-side.png', 'pose-back.png'
          alt={`${view} pose guide`}
          className="w-28 h-auto opacity-90 mb-2"
        />
        <div className="text-white font-medium text-sm text-center px-2">
          {instructionText}
        </div>
      </div>
    </div>
  );
};

export default PoseOverlay;
