import React from 'react';

const Home = () => {
  return (
    <section className="min-h-screen bg-gradient-to-br from-purple-100 via-white to-pink-100 flex items-center justify-center px-4">
      <div className="bg-white/80 backdrop-blur-lg p-10 rounded-2xl shadow-2xl max-w-xl w-full text-center border border-white/30">
        <h1 className="text-4xl md:text-5xl font-extrabold bg-gradient-to-r from-purple-700 to-pink-600 bg-clip-text text-transparent mb-4">
          Smarter Fitting. Styled by AI.
        </h1>
        <p className="text-base md:text-lg text-gray-700 mb-8 leading-relaxed">
          Upload your photo or open your camera. Let our AI measure and recommend your perfect outfit.
        </p>
        <a
          href="/measure"
          className="inline-block bg-gradient-to-r from-purple-600 to-pink-600 text-white px-8 py-4 rounded-full hover:from-purple-700 hover:to-pink-700 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl font-semibold text-lg"
        >
          Get Your Fit
        </a>
      </div>
    </section>
  );
};

export default Home;
