import React, { useRef, useEffect, useState } from 'react';
import { Camera } from '@mediapipe/camera_utils';
import { Pose } from '@mediapipe/pose';
import { drawLandmarks } from '@mediapipe/drawing_utils';
import PoseOverlay from '../components/PoseOverlay';

const Measure = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [step, setStep] = useState('front');
  const [landmarksDetected, setLandmarksDetected] = useState(false);
  const [fullBodyVisible, setFullBodyVisible] = useState(true);
  const [userHeight, setUserHeight] = useState('');
  const [measurementData, setMeasurementData] = useState({
    front: null,
    side: null,
    back: null,
  });

  const getDistance = (p1, p2) => {
    const dx = p1.x - p2.x;
    const dy = p1.y - p2.y;
    return Math.sqrt(dx * dx + dy * dy);
  };

  const calculateMeasurements = () => {
    if (!userHeight || !measurementData.front) return null;
    const landmarks = measurementData.front.landmarks;

    const pixelHeight = getDistance(landmarks[0], landmarks[27]);
    const pixelPerCm = pixelHeight / parseFloat(userHeight);

    const shoulderWidth = getDistance(landmarks[11], landmarks[12]) / pixelPerCm;
    const waistWidth = getDistance(landmarks[23], landmarks[24]) / pixelPerCm;
    const hipWidth = getDistance(landmarks[25], landmarks[26]) / pixelPerCm;

    return {
      shoulder: shoulderWidth.toFixed(1),
      waist: waistWidth.toFixed(1),
      hips: hipWidth.toFixed(1),
    };
  };

  const finalMeasurements = calculateMeasurements();

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext('2d');

    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose@0.4.1646424915/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);

      if (results.poseLandmarks) {
        setLandmarksDetected(true);

        drawLandmarks(canvasCtx, results.poseLandmarks, {
          color: 'red',
          lineWidth: 2,
          radius: 3,
        });

        const hasHips = results.poseLandmarks[23] && results.poseLandmarks[24];
        const hasAnkles = results.poseLandmarks[27] && results.poseLandmarks[28];
        const isFull = hasHips && hasAnkles;
        setFullBodyVisible(isFull);

        if (isFull && !measurementData[step]) {
          setMeasurementData((prev) => ({
            ...prev,
            [step]: {
              timestamp: Date.now(),
              landmarks: results.poseLandmarks,
            },
          }));

          setTimeout(() => {
            if (step === 'front') setStep('side');
            else if (step === 'side') setStep('back');
            else if (step === 'back') setStep('result');
          }, 3000);
        }
      } else {
        setLandmarksDetected(false);
        setFullBodyVisible(true);
      }

      canvasCtx.restore();
    });

    if (videoElement) {
      const camera = new Camera(videoElement, {
        onFrame: async () => {
          await pose.send({ image: videoElement });
        },
        width: 640,
        height: 480,
      });
      camera.start();
    }
  }, [step, measurementData]);

  return (
    <div className="min-h-screen bg-black flex flex-col items-center justify-center text-white px-4 py-6">
      <h1 className="text-3xl font-bold mb-4 text-purple-400">AI Body Measurement</h1>

      <div className="mb-4">
        <label className="mr-2">Your Height (cm):</label>
        <input
          type="number"
          value={userHeight}
          onChange={(e) => setUserHeight(e.target.value)}
          className="px-2 py-1 rounded text-black"
        />
      </div>

      <div className="relative w-[640px] h-[480px] border border-purple-600 rounded-lg overflow-hidden">
        <video ref={videoRef} className="absolute w-full h-full" autoPlay playsInline muted />
        <canvas ref={canvasRef} width="640" height="480" className="absolute top-0 left-0" />

        <div className="absolute inset-0 border-4 border-dashed border-purple-400 pointer-events-none animate-pulse" />
        {step !== 'result' && <PoseOverlay view={step} />}
      </div>

      <p className="mt-4 text-green-400">
        {landmarksDetected ? '‚úÖ Body landmarks detected!' : 'üîç Looking for body...'}
      </p>

      {!fullBodyVisible && (
        <div className="mt-3 text-red-500 font-semibold text-md">
          ‚ö†Ô∏è Please ensure your full body (head to feet) fits inside the purple box.
        </div>
      )}

      {step === 'result' && (
        <div className="mt-6 bg-white text-black p-6 rounded-lg shadow-lg w-full max-w-lg">
          <h2 className="text-xl font-bold text-purple-700 mb-2">üìè Measurement Summary</h2>
          <ul className="text-sm space-y-1">
            <li>‚úÖ Front pose captured: {measurementData.front ? 'Yes' : 'No'}</li>
            <li>‚úÖ Side pose captured: {measurementData.side ? 'Yes' : 'No'}</li>
            <li>‚úÖ Back pose captured: {measurementData.back ? 'Yes' : 'No'}</li>
          </ul>

          {finalMeasurements && (
            <div className="mt-4 text-sm text-gray-800">
              <p>üëï Shoulder Width: {finalMeasurements.shoulder} cm</p>
              <p>üéØ Waist Width: {finalMeasurements.waist} cm</p>
              <p>üçë Hip Width: {finalMeasurements.hips} cm</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Measure;
